INSERT INTO [dbo].[Column] ([ColumnUuid], [IsDeleted], [ID], [ColumnTitleText], [ColumnTitle], [ColumnNum], [AddTime], [AddPeople], [SuperiorUuid], [SuperiorMenu], [ColumnType], [ColumnModel], [ColumnUrl], [ColumnPic], [Staue], [ColumnWord], [ColumnContent], [ColumnVideo], [ColumnAudio], [ColumnFile], [SortID], [IssueTime], [IsStick]) VALUES (N'2613B948-E83C-42C3-8719-B85802BBA1D5', N'0', N'230', NULL, N'基础化学实验（Ⅱ）— 有机化学实验', NULL, N'2020-11-21 19:07:36', N'admin', N'0DB6E193-9843-4E3D-B95B-B0049AE1DD91', N'1', NULL, N'默认栏目', NULL, NULL, NULL, NULL, N'<p>基础化学实验（Ⅱ）—有机化学实验<br>发表于: 2013-6-23 13:46:47</p><p style="margin-left:7px;">&nbsp;</p><p style="margin-left:7px;"><strong>（1）基本操作实验</strong></p><p style="margin-left:7px;">&nbsp;</p><figure class="table"><table><tbody><tr><td><p style="margin-left:7px;">序号</p></td><td><p style="margin-left:7px;">实 验 项 目</p></td><td><p style="margin-left:7px;">每组人数</p></td><td><p style="margin-left:7px;">教&nbsp; 学&nbsp; 内&nbsp; 容</p></td></tr><tr><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">玻璃工操作</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．初步了解玻璃管的一般性质及火焰的掌握。</p><p style="margin-left:7px;">2．学会玻璃管的切割、弯曲、拉制。</p></td></tr><tr><td><p style="margin-left:7px;">2</p></td><td><p style="margin-left:7px;">蒸馏</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．掌握蒸馏的原理，了解其意义。</p><p style="margin-left:7px;">2．掌握蒸馏的操作方法。</p><p style="margin-left:7px;">3．学会用常量法测定液态物质的沸点。</p></td></tr><tr><td><p style="margin-left:7px;">3</p></td><td><p style="margin-left:7px;">分馏</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．掌握分馏的原理，了解其意义。</p><p style="margin-left:7px;">2．掌握分馏的操作方法。</p></td></tr><tr><td><p style="margin-left:7px;">4</p></td><td><p style="margin-left:7px;">减压蒸馏</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．掌握减压蒸馏的原理，了解其意义。</p><p style="margin-left:7px;">2．掌握减压蒸馏的操作方法。</p></td></tr><tr><td><p style="margin-left:7px;">5</p></td><td><p style="margin-left:7px;">薄层色谱</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．学习薄层色谱法的原理，了解其意义和应用。</p><p style="margin-left:7px;">2．掌握薄层色谱的操作方法。</p></td></tr><tr><td><p style="margin-left:7px;">6</p></td><td><p style="margin-left:7px;">柱色谱</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．学习和练习湿法填装色谱柱的方法和操作。</p><p style="margin-left:7px;">2．学习和练习色谱柱的上样操作。</p><p style="margin-left:7px;">3．练习利用色谱柱展开和洗脱被分离物。</p></td></tr><tr><td><p style="margin-left:7px;">7</p></td><td><p style="margin-left:7px;">重结晶</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．学习固体有机物重结晶提纯的原理与方法。</p><p style="margin-left:7px;">2．掌握重结晶操作。</p></td></tr><tr><td><p style="margin-left:7px;">8</p></td><td><p style="margin-left:7px;">熔点测定</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．了解熔点测定的意义。</p><p style="margin-left:7px;">2．掌握测定熔点的操作。</p></td></tr><tr><td><p style="margin-left:7px;">9</p></td><td><p style="margin-left:7px;">红外光谱</p></td><td><p style="margin-left:7px;">2</p></td><td><p style="margin-left:7px;">1．了解红外光谱法测定有机化合物结构的原理。</p><p style="margin-left:7px;">2．采用液膜法和压片法各测定一个有机化合物样品，掌握涂膜、压片及红外检测的操作方法。</p><p style="margin-left:7px;">3．学习初步的谱图解析方法。</p></td></tr></tbody></table></figure><p style="margin-left:7px;">&nbsp;</p><p style="margin-left:7px;"><strong>（2）制备实验</strong></p><p style="margin-left:7px;">&nbsp;</p><figure class="table"><table><tbody><tr><td><p style="margin-left:7px;">序号</p></td><td><p style="margin-left:7px;">实&nbsp;&nbsp; 验&nbsp;&nbsp; 项&nbsp;&nbsp; 目</p></td><td><p style="margin-left:7px;">每组人数</p></td><td><p style="margin-left:7px;">教&nbsp; 学&nbsp; 内&nbsp; 容</p></td></tr><tr><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1–溴丁烷的制备</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．学习以溴化钠、浓硫酸和正丁醇制备1-溴丁烷的原理和方法。</p><p style="margin-left:7px;">2．练习附带吸收有害气体装置的回流加热操作。</p><p style="margin-left:7px;">3．学会分液漏斗的使用，掌握萃取、洗涤、干燥的基本操作及原理。</p></td></tr><tr><td><p style="margin-left:7px;">2</p></td><td><p style="margin-left:7px;">溴乙烷的制备</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1.&nbsp; 掌握制备溴乙烷的原理和方法；</p><p style="margin-left:7px;">2.&nbsp; 掌握反应中产生有害气体的处理方法；</p><p style="margin-left:7px;">3.&nbsp; 巩固蒸馏、分液等基本操作。</p></td></tr><tr><td><p style="margin-left:7px;">3</p></td><td><p style="margin-left:7px;">环己烯的制备</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．了解由环己醇脱水制备环己烯的原理及方法。</p><p style="margin-left:7px;">2．熟悉分馏装置。</p><p style="margin-left:7px;">3．熟练掌握分液漏斗的使用。</p></td></tr><tr><td><p style="margin-left:7px;">4</p></td><td><p style="margin-left:7px;">正丁醚的制备</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．学习醇的分子间失水制备醚的原理和方法。</p><p style="margin-left:7px;">2．掌握分水器的使用原理及使用方法。</p><p style="margin-left:7px;">3．巩固回流、蒸馏、洗涤、干燥等基本操作。</p></td></tr><tr><td><p style="margin-left:7px;">5</p></td><td><p style="margin-left:7px;">苯乙醚的制备</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．学习用Williamson合成法制备苯乙醚的原理和方法；</p><p style="margin-left:7px;">2．巩固减压蒸馏、分液和洗涤等基本操作。</p></td></tr><tr><td><p style="margin-left:7px;">6</p></td><td><p style="margin-left:7px;">乙酸正丁酯的制备及检验</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．掌握制备乙酸正丁酯的原理和方法。</p><p style="margin-left:7px;">2．掌握分水器的使用原理及使用方法。</p><p style="margin-left:7px;">3．巩固回流、蒸馏、洗涤、干燥等基本操作。</p></td></tr><tr><td><p style="margin-left:7px;">7</p></td><td><p style="margin-left:7px;">乙酰水杨酸的制备</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．掌握制备乙酰水杨酸的原理和方法。</p><p style="margin-left:7px;">2．学会乙酰水杨酸的提纯。</p></td></tr><tr><td><p style="margin-left:7px;">8</p></td><td><p style="margin-left:7px;">乙酰苯胺的制备</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．掌握苯胺乙酰化反应的原理和实验操作。</p><p style="margin-left:7px;">2．进一步熟悉固体有机物提纯的方法——重结晶。</p></td></tr><tr><td><p style="margin-left:7px;">9</p></td><td><p style="margin-left:7px;">苯甲酸的制备</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．学习芳烃侧链氧化制备芳酸的原理和方法。</p><p style="margin-left:7px;">2．掌握电动搅拌装置的安装与操作。</p><p style="margin-left:7px;">3．掌握抽滤等基本操作。</p></td></tr><tr><td><p style="margin-left:7px;">10</p></td><td><p style="margin-left:7px;">苯乙酮的制备</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．掌握通过Friedel-Crafts酰基化反应制备芳酮的原理和方法。</p><p style="margin-left:7px;">2．掌握电动搅拌装置的安装与操作。</p><p style="margin-left:7px;">3．巩固气体吸收、回流、萃取、干燥、蒸馏等基本操作。</p></td></tr><tr><td><p style="margin-left:7px;">11</p></td><td><p style="margin-left:7px;">邻叔丁基对苯二酚的制备</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1． 学习制备邻叔丁基对苯二酚的原理与方法。</p><p style="margin-left:7px;">2． 熟练点动搅拌、回流、重结晶等实验操作。</p></td></tr><tr><td><p style="margin-left:7px;">12</p></td><td><p style="margin-left:7px;">二苯甲酮的制备</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．学习用傅克反应合成二苯甲酮的方法；</p><p style="margin-left:7px;">2．巩固无水反应和气体吸收的操作方法；</p><p style="margin-left:7px;">3．巩固减压蒸馏、分液和洗涤等基本操作。</p></td></tr><tr><td><p style="margin-left:7px;">13</p></td><td><p style="margin-left:7px;">2–甲基–2–丁醇的制备</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．学习格氏试剂的制备和应用。</p><p style="margin-left:7px;">2．初步掌握低沸点易燃液体的处理方法。</p><p style="margin-left:7px;">3．学会旋转蒸发仪的使用。</p></td></tr><tr><td><p style="margin-left:7px;">14</p></td><td><p style="margin-left:7px;">三苯甲醇的制备</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．掌握格氏试剂的制备方法及应用。</p><p style="margin-left:7px;">2．巩固无水反应的操作方法。</p><p style="margin-left:7px;">3．学习水蒸气蒸馏和重结晶等基本操作。</p></td></tr><tr><td><p style="margin-left:7px;">15</p></td><td><p style="margin-left:7px;">肉桂酸的制备</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．学习肉桂酸的制备原理和方法。</p><p style="margin-left:7px;">2．学习水蒸气蒸馏的原理及其应用，掌握水蒸气蒸馏的装置及操作方法。</p></td></tr><tr><td><p style="margin-left:7px;">16</p></td><td><p style="margin-left:7px;">乙酰乙酸乙酯的制备</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．掌握用Claisen酯缩合制备乙酰乙酸乙酯的原理和方法；</p><p style="margin-left:7px;">2．学习无水反应的操作方法；</p><p style="margin-left:7px;">3．巩固减压蒸馏、分液和洗涤等基本操作。</p></td></tr><tr><td><p style="margin-left:7px;">17</p></td><td><p style="margin-left:7px;">氯苯的制备</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．掌握重氮化反应的原理及反应条件的控制。</p><p style="margin-left:7px;">2．了解桑德迈尔（Sandmeyer）反应制备氯苯的方法。</p></td></tr><tr><td><p style="margin-left:7px;">18</p></td><td><p style="margin-left:7px;">间硝基苯胺的制备</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．掌握芳香族多硝基化合物的选择性还原的原理与应用。</p><p style="margin-left:7px;">2．巩固机械搅拌、回流，减压过滤、重结晶等基本操作。</p></td></tr><tr><td><p style="margin-left:7px;">19</p></td><td><p style="margin-left:7px;">苯甲醇和苯甲酸的制备</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．学习由苯甲醛制备苯甲醇和苯甲酸的原理和方法。</p><p style="margin-left:7px;">2．熟悉磁力搅拌仪的使用。</p><p style="margin-left:7px;">3．进一步掌握萃取、洗涤、蒸馏、干燥和重结晶等基本操作。</p><p style="margin-left:7px;">4．学会低沸点溶剂的处理。</p></td></tr><tr><td><p style="margin-left:7px;">20</p></td><td><p style="margin-left:7px;">呋喃甲醇与呋喃甲酸的制备</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．学习利用Cannizzaro反应制备呋喃甲醇与呋喃甲酸的原理与方法。</p><p style="margin-left:7px;">2．熟练掌握低沸点溶剂的处理。</p></td></tr></tbody></table></figure><p style="margin-left:7px;">&nbsp;</p><p style="margin-left:7px;"><br>&nbsp;</p><p style="margin-left:7px;"><strong>（3）综合实验</strong></p><p style="margin-left:7px;">&nbsp;</p><figure class="table"><table><tbody><tr><td><p style="margin-left:7px;">序号</p></td><td><p style="margin-left:7px;">实 验 项 目</p></td><td><p style="margin-left:7px;">每组人数</p></td><td><p style="margin-left:7px;">教&nbsp; 学&nbsp; 内&nbsp; 容</p></td></tr><tr><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">苯甲酸的微波合成及苯甲酸乙酯的制备（微型）</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．学习苯甲酸的微波合成方法。</p><p style="margin-left:7px;">2．掌握制备苯甲酸乙酯的原理和实验方法。</p><p style="margin-left:7px;">3．掌握分水器的使用，巩固萃取、回流等基本操作。</p><p style="margin-left:7px;">4．学习半微量有机合成实验操作。</p></td></tr><tr><td><p style="margin-left:7px;">2</p></td><td><p style="margin-left:7px;">室温离子液体中乙酸苄酯的合成</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．了解室温离子液体的含义及其在有机合成中的应用。</p><p style="margin-left:7px;">2．掌握用乙酸钠和氯苄催化合成乙酸苄酯的方法。</p></td></tr><tr><td><p style="margin-left:7px;">3</p></td><td><p style="margin-left:7px;">无水无氧条件下二碘化钐促进的苯甲醛偶联反应</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．学习无水无氧条件下的有机合成实验。</p><p style="margin-left:7px;">2．掌握了解苯甲醛偶联反应的原理及实验方法。</p></td></tr><tr><td><p style="margin-left:7px;">4</p></td><td><p style="margin-left:7px;">3-吲哚羧酸的制备</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．掌握3-吲哚羧酸的制备原理和实验方法。</p><p style="margin-left:7px;">2．学习杂环化合物的有关反应。</p></td></tr><tr><td><p style="margin-left:7px;">5</p></td><td><p style="margin-left:7px;">二苯基羟乙酮的绿色合成</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．了解安息香缩合反应的原理。</p><p style="margin-left:7px;">2．建立初步的绿色化学概念。</p><p style="margin-left:7px;">3．学习和巩固重结晶、熔点测定、红外光谱检测等操作。</p></td></tr><tr><td><p style="margin-left:7px;">6</p></td><td><p style="margin-left:7px;">二苯基乙二酮的制备</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．学习用温和的氧化剂氧化安息香制备α-二酮的原理与方法。</p><p style="margin-left:7px;">2．掌握用薄层板跟踪反应进程。</p></td></tr><tr><td><p style="margin-left:7px;">7</p></td><td><p style="margin-left:7px;">二苯基乙醇酸的制备</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．了解二苯基乙醇酸重排的原理。</p><p style="margin-left:7px;">2．巩固重结晶、熔点测定、红外光谱检测等操作。</p></td></tr><tr><td><p style="margin-left:7px;">8</p></td><td><p style="margin-left:7px;">二苯基乙醇酸交酯的制备 (微型)</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．了解α-羟基酸脱水成交酯的原理。</p><p style="margin-left:7px;">2．学习半微量有机合成实验操作。</p></td></tr><tr><td><p style="margin-left:7px;">9</p></td><td><p style="margin-left:7px;">甲基红的合成</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．学习霍夫曼反应、重氮化反应、偶联反应的原理。</p><p style="margin-left:7px;">2．掌握合成甲基红的实验操作。</p></td></tr><tr><td><p style="margin-left:7px;">10</p></td><td><p style="margin-left:7px;">磺胺类药物的合成</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．学习合成磺胺类化合物的原理与方法。</p><p style="margin-left:7px;">2．掌握合成磺胺类药物的实验操作。</p></td></tr><tr><td><p style="margin-left:7px;">11</p></td><td><p style="margin-left:7px;">3（2<i>H</i>）-哒嗪酮类衍生物的合成</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．了解相关杂环反应的基本原理与实验方法。</p><p style="margin-left:7px;">2．培养综合分析问题和解决问题的能力。</p><p style="margin-left:7px;">3．掌握实验中涉及的基本操作。</p></td></tr><tr><td><p style="margin-left:7px;">12</p></td><td><p style="margin-left:7px;">1-（4-甲基）苯基-3-苯基-3-（N-苯基）氨基-1-丙酮的制备</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．学习1-（4-甲基）苯基-3-苯基-3-（N-苯基）氨基-1-丙酮的制备原理及方法。</p><p style="margin-left:7px;">2. 掌握Friedel-Crafts酰基化反应、Claisen-Schmidt缩合反应以及α，β-不饱和酮加成反应的原理。</p></td></tr><tr><td><p style="margin-left:7px;">13</p></td><td><p style="margin-left:7px;"><i>α</i>-苯乙胺外消旋体的拆分</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1. 掌握拆分含一个手性碳的外消旋体的方法及运用分步结晶法得到两个对映体的方法。</p><p style="margin-left:7px;">2. 熟悉使用旋光仪测定物质旋光度的方法及对光学活性物质纯度的初步评价。</p></td></tr><tr><td><p style="margin-left:7px;">14</p></td><td><p style="margin-left:7px;">从茶叶中提取咖啡碱</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1．学习从茶叶中提取咖啡碱的原理和方法。</p><p style="margin-left:7px;">2．进一步掌提升华操作和索氏提取器的原理及使用方法。</p></td></tr></tbody></table></figure><p style="margin-left:7px;">&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p>', NULL, NULL, NULL, N'0', NULL, N'0')
