INSERT INTO [dbo].[Content] ([ColumnUuid], [IsDeleted], [ID], [ColumnTitleText], [ColumnTitle], [ColumnNum], [AddTime], [AddPeople], [SuperiorUuid], [SuperiorMenu], [ColumnType], [ColumnModel], [ColumnUrl], [ColumnPic], [Staue], [ColumnWord], [ColumnContent], [ColumnVideo], [ColumnAudio], [ColumnFile], [SortID], [IssueTime], [IsStick]) VALUES (N'71283054-CD86-4EBF-9543-8A44195EB4DB', N'0', N'34', N'实验进程', N'关于实验补课的通知', NULL, N'2013-10-08 10:06:25.000', N'admin', N'626386FF-F75E-4966-A7E8-F017E895B0BB', N'2', NULL, N'列表栏目', N'upload/Zhongxin/201310081006212365.doc', NULL, N'1', NULL, N'<p style="line-height: 150%; margin: 0cm 0cm 0pt" class="MsoNormal"><span style="font-family: 宋体; mso-ascii-font-family: ''Times New Roman''; mso-hansi-font-family: ''Times New Roman''"><font size="2">各有关同学：</font></span></p>
<p style="line-height: 150%; text-indent: 21.75pt; margin: 0cm 0cm 0pt" class="MsoNormal"><font size="2"><span style="font-family: 宋体; mso-ascii-font-family: ''Times New Roman''; mso-hansi-font-family: ''Times New Roman''">因受台风&ldquo;菲特&rdquo;的影响，按学校通知本周二（</span><st1:chsdate isrocdate="False" islunardate="False" day="8" month="10" year="2013" w:st="on"><span lang="EN-US"><font face="Times New Roman">10</font></span><span style="font-family: 宋体; mso-ascii-font-family: ''Times New Roman''; mso-hansi-font-family: ''Times New Roman''">月</span><span lang="EN-US"><font face="Times New Roman">8</font></span><span style="font-family: 宋体; mso-ascii-font-family: ''Times New Roman''; mso-hansi-font-family: ''Times New Roman''">日</span></st1:chsdate><span style="font-family: 宋体; mso-ascii-font-family: ''Times New Roman''; mso-hansi-font-family: ''Times New Roman''">）停课，原安排的基础化学实验将在本周日（</span><st1:chsdate isrocdate="False" islunardate="False" day="13" month="10" year="2013" w:st="on"><span lang="EN-US"><font face="Times New Roman">10</font></span><span style="font-family: 宋体; mso-ascii-font-family: ''Times New Roman''; mso-hansi-font-family: ''Times New Roman''">月</span><span lang="EN-US"><font face="Times New Roman">13</font></span><span style="font-family: 宋体; mso-ascii-font-family: ''Times New Roman''; mso-hansi-font-family: ''Times New Roman''">日</span></st1:chsdate><span style="font-family: 宋体; mso-ascii-font-family: ''Times New Roman''; mso-hansi-font-family: ''Times New Roman''">）补上。实验时间上午</span><span lang="EN-US"><font face="Times New Roman">8</font></span><span style="font-family: 宋体">∶</span><span lang="EN-US"><font face="Times New Roman">00</font></span><span style="font-family: 宋体; mso-ascii-font-family: ''Times New Roman''; mso-hansi-font-family: ''Times New Roman''">开始，下午</span><span lang="EN-US"><font face="Times New Roman">13</font></span><span style="font-family: 宋体">∶</span><span lang="EN-US"><font face="Times New Roman">30</font></span><span style="font-family: 宋体; mso-ascii-font-family: ''Times New Roman''; mso-hansi-font-family: ''Times New Roman''">开始。</span></font></p>
<p style="line-height: 150%; text-indent: 21.75pt; margin: 0cm 0cm 0pt" class="MsoNormal"><span style="font-family: 宋体; mso-ascii-font-family: ''Times New Roman''; mso-hansi-font-family: ''Times New Roman''"><font size="2">具体安排如下：</font></span></p>
<table style="border-bottom: medium none; border-left: medium none; width: 446.4pt; border-collapse: collapse; border-top: medium none; border-right: medium none; mso-border-alt: solid windowtext .5pt; mso-yfti-tbllook: 480; mso-padding-alt: 0cm 5.4pt 0cm 5.4pt; mso-border-insideh: .5pt solid windowtext; mso-border-insidev: .5pt solid windowtext" class="MsoTableGrid" border="1" cellspacing="0" cellpadding="0" width="595">
    <tbody>
        <tr style="height: 23.4pt; mso-yfti-irow: 0; mso-yfti-firstrow: yes">
            <td style="border-bottom: windowtext 1pt solid; border-left: windowtext 1pt solid; padding-bottom: 0cm; background-color: transparent; padding-left: 5.4pt; width: 167.4pt; padding-right: 5.4pt; height: 23.4pt; border-top: windowtext 1pt solid; border-right: windowtext 1pt solid; padding-top: 0cm; mso-border-alt: solid windowtext .5pt" rowspan="2" width="223">
            <p style="text-align: center; line-height: 150%; margin: 0cm 0cm 0pt" class="MsoNormal" align="center"><span style="font-family: 宋体; mso-ascii-font-family: ''Times New Roman''; mso-hansi-font-family: ''Times New Roman''"><font size="2">实验课程</font></span></p>
