INSERT INTO [dbo].[Content] ([ColumnUuid], [IsDeleted], [ID], [ColumnTitleText], [ColumnTitle], [ColumnNum], [AddTime], [AddPeople], [SuperiorUuid], [SuperiorMenu], [ColumnType], [ColumnModel], [ColumnUrl], [ColumnPic], [Staue], [ColumnWord], [ColumnContent], [ColumnVideo], [ColumnAudio], [ColumnFile], [SortID], [IssueTime], [IsStick]) VALUES (N'A9516044-58A3-4FF0-B545-DC98C895BACC', N'0', N'135', N'省级精品课程', N'基础化学实验（Ⅰ）—无机及分析化学实验', NULL, N'2020-11-21 17:48:26', N'admin', N'3616816A-391C-4346-9BE0-5D4A799FBCC8', N'2', NULL, N'列表栏目', NULL, NULL, N'1', NULL, N'<p>基础化学实验（Ⅰ）—无机及分析化学实验<br>发表于: 2013-6-23 13:54:17</p><p style="margin-left:7px;">&nbsp;</p><p style="margin-left:7px;"><strong>（1）基本操作实验</strong></p><p style="margin-left:7px;">&nbsp;</p><figure class="table"><table><tbody><tr><td><p style="margin-left:7px;">序号</p></td><td><p style="margin-left:7px;">实&nbsp;&nbsp; 验&nbsp;&nbsp; 项&nbsp;&nbsp; 目</p></td><td><p style="margin-left:7px;">每组人数</p></td><td><p style="margin-left:7px;">教&nbsp; 学&nbsp; 内&nbsp; 容</p></td></tr><tr><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">化学实验仪器准备</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1、熟悉仪器名称、规格，掌握玻璃仪器洗涤方法。</p><p style="margin-left:7px;">2、认识仪器洗涤在分析化学实验中的重要作用，洗净一套符合分析要求的仪器。</p><p style="margin-left:7px;">3、了解常用洗涤剂的配制方法。</p></td></tr><tr><td><p style="margin-left:7px;">2</p></td><td><p style="margin-left:7px;">氯化钠的提纯</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1、熟悉溶解、沉淀、过滤、蒸发、浓缩、结晶和干燥等基本操作。</p><p style="margin-left:7px;">2、掌握提纯NaCl的原理和方法。</p><p style="margin-left:7px;">3、了解SO42-、Ca2+、Mg2+等离子的定性鉴定。</p></td></tr><tr><td><p style="margin-left:7px;">3</p></td><td><p style="margin-left:7px;">分析天平的使用及称量练习</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1、了解电子天平的使用规则。</p><p style="margin-left:7px;">2、准确掌握减量法的称量方法。</p></td></tr><tr><td><p style="margin-left:7px;">4</p></td><td><p style="margin-left:7px;">容量仪器的校正</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1、掌握滴定管、容量瓶、移液管的使用方法。</p><p style="margin-left:7px;">2、练习滴定管、容量瓶、移液管的校准方法，并了解容量器皿校准的意义。</p></td></tr><tr><td><p style="margin-left:7px;">5</p></td><td><p style="margin-left:7px;">酸碱标准溶液的配制和比较</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1、掌握NaOH和HCl标准溶液的配制方法。</p><p style="margin-left:7px;">2、练习、掌握滴定操作及终点的判断。</p></td></tr><tr><td><p style="margin-left:7px;">6</p></td><td><p style="margin-left:7px;">酸碱标准溶液的标定</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">1、进一步练习滴定操作。</p><p style="margin-left:7px;">2、学习酸碱标准溶液的滴定方法。</p></td></tr><tr><td><p style="margin-left:7px;">7</p></td><td><p style="margin-left:7px;">硫代硫酸钠标准溶液的配制与标定</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">掌握Na2S2O3标准溶液的配制、标定及保存方法。</p></td></tr><tr><td><p style="margin-left:7px;">8</p></td><td><p style="margin-left:7px;">溶液pH值的电位测定</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">了解并初步掌握检验玻璃电极性能的方法及pH电位的测定方法。</p></td></tr><tr><td><p style="margin-left:7px;">9</p></td><td><p style="margin-left:7px;">弱电解质电离常数的测定</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">测定醋酸的电离常数，正确使用pH计。</p></td></tr><tr><td><p style="margin-left:7px;">10</p></td><td><p style="margin-left:7px;">I3– = I2&nbsp;- + I– 体系平衡常数的测定</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">测定I3－＝ I2－&nbsp;＋ I－&nbsp;体系的平衡常数，滴定操作。</p></td></tr><tr><td><p style="margin-left:7px;">11</p></td><td><p style="margin-left:7px;">硫酸钙溶度积的测定</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">离子交换法测定硫酸钙的溶解度， pH计的使用。</p></td></tr><tr><td><p style="margin-left:7px;">12</p></td><td><p style="margin-left:7px;">过氧化氢分解速率常数及活化能的测定</p></td><td><p style="margin-left:7px;">1</p></td><td><p style="margin-left:7px;">测定过氧化氢的分解速率，求出过氧化氢分解反应的速率常数和活化能，练习滴定操作。</p></td></tr></tbody></table></figure><p>&nbsp;</p><p>&nbsp;</p>', NULL, NULL, NULL, N'0', N'2013-06-23', N'0')
GO

