INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'5C0E9D69-F4AC-4AB2-BDAA-C278360581C5', N'0', N'5', N'', N'莫干山，我们来了｜化学化工实验中心启动实验室搬迁工作', N'<p>&nbsp;</p><h2 style="margin-left:0px;text-align:center;"><strong>莫干山，我们来了｜化学化工实验中心启动实验室搬迁工作</strong></h2><p style="text-align:center;">发表于: 2020/9/24 15:40:15</p><p style="margin-left:0px;text-align:center;">齐心协力，搬迁启动</p><p style="margin-left:0px;">新学期伊始，化学化工实验教学中心即投入到紧张的搬迁工作中。近日，实验室与资产管理处、学院负责人陪同副校长虞晓芬一行来实验中心慰问忙碌中的中心老师。虞晓芬认真察看了搬迁中的实验室，听取了实验中心搬迁整体情况，详细了解了搬迁过程中存在的困难，对中心老师辛劳的付出给予肯定。目前，化工原理实验室已于9月7日完成实验设备拆装、打包环节，并于9月11日完成部分搬迁。9月18日将进行学院机房搬迁，9月24日至26日将进行后批次实验设备搬迁.</p><figure class="image"><img src="[object Object]"></figure><p>&nbsp;</p><p>凝聚共识，办法总比困难多</p><p>化学化工实验中心负责人强根荣老师谈到，莫干山校区实验室建设时间紧、困难多，各个工序的施工队交叉作业，实验室内部的水电、弱电、通风、消防设施、安全设施等管网交叉复杂，必须要通盘考虑设计。为此，中心教师在2018年暑期就前往多地，了解考察相关的学校和实验室建设装备公司，学习建设经验，厘清注意事项。在今年暑期实验室安装建设过程中，实验室与资产处及学院相关领导给予了充分协调和极大支持。中心安排值班表，每隔一天就有一位老师前往工地察看进度，及时发现、纠正建设中的问题。中心承担着全校化学化工的实验教学任务，涉及面广、仪器设备多，低值易碎的玻璃仪器必须逐一包扎，而一些大型设备无法整体搬迁，必须分拆后到新校区重新安装，投入的财力、人力都是巨大的。因此，中心举全员之 力统一协调，群策群力，努力寻找可解决的方案。</p><figure class="image"><img src="http://192.168.0.226:4062/UploadFiles/NesFile/Picture/20201120_131346071_9ad456fe-96a7-44c1-b6a4-babbe5e7b533.jpg"></figure><p style="text-align:center;">未来可期，搬好更要用好</p><p>“尽快搬、尽快用”，当然从“搬过来”到“用起来”，还有许多工作要完成。聚焦本次实验室搬迁工作，化工人始终做“艰苦创业、开拓创新、争创一流”的“三创精神”守护者、践行者，必将努力完成从杭州校区到莫干山校区近40公里的接力跑活动。莫干山校区，我们来了！</p>', N'245C2470-7C0E-4894-B2D4-2DE5186E2D6E', N'20201210_115042046_9de3926f-ad18-4c59-b9fc-90bc8eadb977.jpg', N'2020-09-24 15:40:15', N'1', N'2020-11-20', N'admin', N'1', N'', N'', N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'537899CD-B1B3-45FA-A96E-F3826EC3F703', N'0', N'6', N'', N'化学实验教学中心本学期初连续开展教研活动', N'<h2 style="margin-left:40px;text-align:center;">化学实验教学中心本学期初连续开展教研活动</h2><p style="text-align:center;">发表于: 2019/3/12 8:48:23</p><p style="margin-left:0px;">3月7日，化学实验教学中心在屏峰校区举行教研活动。针对“基础化学实验I”新的教学大纲中的教学内容、教学重点难点等进行了交流，对教学过程中一些常见的问题以及如何有效地开展针对性的教学进行了分析，任课教师预做了本学期更新的实验内容，为更好地落实教学过程做好了准备。</p><p style="margin-left:0px;">3月8日，化学实验教学中心在朝晖校区针对基础化学实验Ⅱ的有关教学问题，再次举行活动，围绕“乙酰二茂铁的制备及柱色谱分离”、“苯甲酸（微波）及苯甲酸乙酯的制备、减压蒸馏”等实验，对研究生助教和新聘任的指导教师进行了培训。</p><p style="margin-left:0px;">&nbsp; &nbsp; 教学活动的顺利开展，为课程教学交流提供了良好的交流平台，有利于各任课教师教学经验的相互交流、学习，能更好的汇总各任课教师在教学过程中一些常见的教学问题，通过教学团队各老师的共同协作交流来有效地解决。</p><figure class="image"><img src="http://hxsyzx.dodokon.com/Tools/kindeditor/attached/image/20190312/20190312084529_5952.png" alt=""></figure><p>&nbsp;&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/Tools/kindeditor/attached/image/20190312/20190312084548_0677.png" alt=""></figure><figure class="image"><img src="http://hxsyzx.dodokon.com/Tools/kindeditor/attached/image/20190312/20190312084606_2258.png" alt=""></figure><p>&nbsp;&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/Tools/kindeditor/attached/image/20190312/20190312084620_0724.png" alt=""></figure><p style="margin-left:0px;">&nbsp;</p>', N'245C2470-7C0E-4894-B2D4-2DE5186E2D6E', N'', N'2019-03-12 08:48:23', N'1', N'2020-11-20', N'admin', N'2', N'', N'', N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'D5FB88D2-70DF-40DE-ACAB-41914E9C4D47', N'0', N'7', N'', N'中国人民警察大学基础课实验中心来访', N'<h2 style="margin-left:0px;text-align:center;">中国人民警察大学基础课实验中心来访</h2><p style="text-align:center;">发表于: 2018/11/7 16:35:33</p><p style="margin-left:0pt;">&nbsp;&nbsp;&nbsp;&nbsp; 2018年11月7日，中国人民警察大学（原武警学院）基础课实验中心副主任王永明教授一行，来化学实验教学中心参观交流。化学化工实验教学中心副主任强根荣接待了来访者，向客人们介绍了我校化学实验教学中心在实验室建设、课程建设、实验室管理等方面的情况，双方就实验室建设的理念、化学实验教学改革、实验考核、实验设备以及实验室开放、大学生课外科技活动等进行了深入、细致地交流。客人们表示此次来访学到了不少经验，收获很大，对我校实验中心自制的减压蒸馏小推车等颇感兴趣，期望用于他们的教学、科研实验中，以后将加强交流，促进共同提高。</p><p style="margin-left:0pt;">&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/Tools/kindeditor/attached/image/20181107/20181107155952_6219.jpg" alt=""></figure><p>&nbsp;</p>', N'C3285CB0-CBC1-4B2D-B519-FB37C8D557D6', N'', N'2018/11/7 16:35:33', N'1', N'2020-11-20', N'admin', N'3', N'', N'', N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'78FEF041-B36E-4B71-8257-C921C1F7D2BD', N'0', N'8', N'', N'化学实验教学中心扎实开展教研活动', N'<h2 style="text-align:center;">化学实验教学中心扎实开展教研活动</h2><p style="text-align:center;">发表于: 2018/3/29 14:14:34</p><p>&nbsp; &nbsp;本学期以来，化学实验教学中心已先后开展了多次教学研讨活动，分别对年轻教师和研究生助教有针对性地进行示范或培训。<br>&nbsp; &nbsp;近几年来，参与化学实验教学的年轻教师越来越多，中心非常重视这些教师教学能力的稳步提高。本学期开学以后，围绕“如何更好地进行实验教学指导，提高教学质量”这个主题，组织近几年引进的青年教师认真聆听了强根荣老师关于“乙酰二茂铁的制备及柱色谱分离”的实验授课，耳濡目染了强老师在教学过程中精湛的教学方法、娴熟的内容设计及灵活的授课方式，并由老教师杨振平主持在课后进行了研讨。青年教师们都认为：①引课内容非常重要，精彩的引课内容能提高学生对整堂课的学习热情；②教学内容的娴熟运用取决于对内容掌握的深度、广度及对学生学情的把控，需要平时不断的积累；③教学过程中要更多地采用“启发式”、“讨论式”等教学方法，以促进学生由“被动学”转变为“主动学”。<br>&nbsp; &nbsp;针对本学期基础化学实验Ⅱ（有机化学实验）开设的实验内容综合性强、跨度大、难度高、时间长等特点，在学校的支持下，中心聘请了六位研究生助教，协助实验主讲教师完成“乙酰二茂铁的制备及柱色谱分离”、“苯甲酸（微波）与苯甲酸乙酯的制备及减压蒸馏”、“苯甲醇与苯甲酸的制备”等大型综合实验的指导，协助处理实验中的突发事情，保障实验平稳有序进行，保障实验室安全。为此，中心对助教进行了严格培训，一方面提高助教们的助教能力，主讲教师也可以腾出时间来强化实验指导，另一方面也锻炼了助教们自身的实践能力和工作经验，取得一举多得的功效。<br>&nbsp; &nbsp;今后，化学实验教学中心将在实验室建设、课程建设等方面进一步拓宽教研范围，采用丰富多彩的教研形式，把教研活动扎扎实实开展下去。</p>', NULL, N'', N' 2018/3/29 14:14:34', N'1', N'2020-11-20', N'admin', N'4', N'', N'', N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'CFEE1833-6C5C-49E1-8963-8A7D3D5A9E87', N'0', N'9', N'', N'化学实验教学中心被评为校级优秀基层教学组织', N'<h2 style="text-align:center;">化学实验教学中心被评为校级优秀基层教学组织</h2><p style="text-align:center;">发表于: 2018/3/5 15:36:05</p><p>&nbsp;&nbsp;&nbsp; 为深化学校教育教学改革，推进本科教育“品质教学”工作，不断提高教学质量，学校组织评选了2017年度优秀基层教学组织。经基层教学组织负责人申报，学院推荐，校内外专家评审，确定全校15个基层教学组织为2017年度校级优秀基层教学组织，化学实验教学中心名列其中。<br>&nbsp;&nbsp;&nbsp; 化学实验教学中心2017年承担了近20个专业、2038人的基础化学实验教学任务，实验人时数183368。支撑了化学工程与工艺、食品科学与工程、安全工程等专业的专业认证与复评等工作。<br>&nbsp;&nbsp;&nbsp; 作为浙江省大学生化学竞赛秘书处，实验中心协调、指导第九届浙江省大学生化学竞赛的具体事宜及命题审查等，中心教师还指导了本校学生参赛。<br>&nbsp;&nbsp;&nbsp; 中心承担包括省教改、省课改在内的教学改革项目8项，教学改革的成果在《浙江教育报》（2017年3月27日）、《中国教育报》（2017年7月24日）、中国教育新闻网（<a href="http://www.jyb.cn/zgjyb/201707/t20170724_716583.html">http://www.jyb.cn/zgjyb/201707/t20170724_716583.html</a>）、中国教育和科研计算机网（<a href="http://www.edu.cn/ke_yan_yu_fa_zhan/gao_xiao_cheng_guo/gao_xiao_zi_xun/201707/t20170724_1542771.shtml">http://www.edu.cn/ke_yan_yu_fa_zhan/gao_xiao_cheng_guo/gao_xiao_zi_xun/201707/t20170724_1542771.shtml</a>）等媒体得到宣传、报道。积极开展教研活动，参加由中国高教学会、国家级实验教学示范中心联席会组织的国内教学会议8人次。<br>&nbsp;&nbsp;&nbsp; 中心对新引进的教师和新聘任的实验指导教师通过集体备课、讲课示范等形式进行培训，促进教学质量的提高。同时，采用“走出去、请进来”等多种方式，积极吸取国内院校的先进教学理念、教学方法，改进实验教学手段。<br>&nbsp;&nbsp;&nbsp; 中心在做好日常教学工作的同时，积极开展对外交流。协助省总工会做好全省职工技能大赛化学检验工比赛，以及衢州市化学检验工比赛的命题、裁判等工作，拓展示范中心的功能，为地方经济和社会发展服务。协助做好中学生化学奥林匹克竞赛工作，为浙江省杭州第二中学、杭州学军中学参加省级选拔赛培训实验。杭二中学生21人、学军中学9人获第31届中国化学会奥林匹克（初赛）浙江赛区一等奖。杭二中学生在第31届中国化学奥林匹克决赛中取得4块金牌、1块银牌的优异成绩。<br>&nbsp;&nbsp;&nbsp; 2017年实验中心任课教师承担的教学班级均获优课优酬奖励。</p>', NULL, N'', N'2018/3/5 15:36:05', N'1', N'2020-11-20', N'admin', N'5', N'', N'', N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'1B6FCEC0-EFF2-4CEC-9692-D198BE9F4CD8', N'0', N'10', N'', N'化学实验教学中心举行新聘任指导教师培训活动', N'<h2 style="text-align:center;">化学实验教学中心举行新聘任指导教师培训活动</h2><p style="text-align:center;">发表于: 2017/11/22 10:37:07</p><p>&nbsp;&nbsp;&nbsp; 11月17日下午，化学实验教学中心举办新聘任实验指导教师培训活动。三位新聘任的实验指导教师分别讲解了环己烯的制备、肉桂酸的制备、乙酰苯胺的制备及重结晶三个实验。化学实验教学中心负责人及学院督导组老师到场听课，针对新教师讲课过程中存在的不足进行了指导，并现场进行了示范。新教师们都认为这样的活动很有必要、很及时，对锻炼他们的讲课、实验指导能力起到了极大的帮助作用，希望今后经常开展类似的培训。</p><p>&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/1.png" alt=""></figure><p>&nbsp;&nbsp;&nbsp;&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/2.png" alt=""></figure><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/3.png" alt=""></figure><p>&nbsp;&nbsp;&nbsp;&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/4.png" alt=""></figure>', NULL, N'', N'2017/11/22 10:37:07', N'1', N'2020-11-20', N'admin', N'6', N'', N'', N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'8F4ECCA2-C8C5-4087-B9CA-AEDEE9EC0368', N'0', N'11', N'', N'化学化工实验教学中心召开全体教师工作会议', N'<h2 style="text-align:center;">化学化工实验教学中心召开全体教师工作会议</h2><p style="text-align:center;">发表于: 2015/5/6 10:30:09</p><p style="margin-left:0cm;">5月5日下午，化学化工实验教学中心全体教师工作会议在存中楼804召开。会议由学院副院长、化学化工实验教学中心常务副主任艾宁主持，化学化工实验教学中心全体教师参加了会议。</p><p style="margin-left:0cm;">艾宁副院长回顾了中心近年来取得的辉煌成绩，特别是化学化工国家级虚拟仿真实验教学中心和化学化工国家级实验教学示范中心的申报成功，标志着实验中心进入了一个新的发展阶段。他对实验中心的队伍建设和教师个人发展、实验中心的示范辐射作用、实验中心目前存在的问题和今后的工作方向等作了指示。中心副主任强根荣、杨阿三老师针对各自分管的工作作了补充，并对今后的发展提了建议。各位教师就中心的建设和发展进行了热烈讨论。会议还布置了化学化工实验教学中心的近期工作。</p><p style="margin-left:0cm;">浙江工业大学化学化工实验教学中心由省级化学实验教学示范中心和省级化工实验教学示范中心为基础组建而成，通过不断的建设发展，2013年建成国家级虚拟仿真实验教学中心，2014年获批国家级实验教学示范中心。目前，中心面向全校21个本科专业开设实验课程13门，其中，“基础化学实验”是国家级精品课程和国家级精品资源共享课程，“化工综合实验”是校级优秀课程，每年承担的实验教学人时数达到23万。</p>', NULL, N'', N'2015/5/6 10:30:09', N'1', N'2020-11-20', N'admin', N'7', N'', N'', N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'3D578C95-DC95-476B-9397-BBAE27ABF645', N'0', N'12', N'', N'化学实验教学中心新增两项校级实验室工作研究与改革项目', N'<h2 style="text-align:center;">化学实验教学中心新增两项校级实验室工作研究与改革项目</h2><p style="text-align:center;">发表于: 2015/4/9 9:03:15</p><p>&nbsp;&nbsp;&nbsp; 日前，实验室与资产管理处发文公布了2015年校级实验室工作研究与改革项目（浙工大资[2015]1号），全校共有37个项目立项，化工学院立项4项。化学实验教学中心强根荣老师申报的《基础化学实验教学内容开发》和梁秋霞老师申报的《用于蔗糖水解实验的恒温手动旋光仪的设计开发》获得立项。<br>&nbsp;&nbsp;&nbsp; 《基础化学实验教学内容开发》项目将立足于现有的基础化学实验教学内容，淘汰部分不适合现代社会发展、环境需求的实验，开发、充实新型的实验内容，同时，进一步完善、补充国家精品资源共享课程的特色内容-基础化学系列性实验，加强在化学一级学科内跨二级学科实验内容的综合。通过这些研究、内容开发以及后续的课程建设工作，做“实”、做“全”基本技能实验，做“大”、做“精”系列性实验，做“深”、做“强”综合性实验，做“活”、做“新”研究探索性实验，为基础化学实验课程的进一步发展、人才培养质量的不断提升起好步、铺好路。<br>&nbsp;&nbsp;&nbsp; 《用于蔗糖水解实验的恒温手动旋光仪的设计开发》项目拟从实际教学应用的需求出发，设计出恒温效果最佳、实际可行性最高的手动恒温旋光仪，并将其应用于本科生的蔗糖水解实验。该项目的设计建立在现有仪器的基础上，不仅可以节约大量仪器经费，也可以在满足学生本科实验需求的同时让学生对仪器有更深刻的认识。</p>', NULL, N'', N' 2015/4/9 9:03:15', N'1', N'2020-11-20', N'admin', N'8', N'', N'', N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'8EA4FC2F-231F-4D06-BFF9-BDDA46920590', N'0', N'13', N'', N'“浙江省大学生化学竞赛”被列为省级大学生科技竞赛项目', N'<h2 style="text-align:center;">“浙江省大学生化学竞赛”被列为省级大学生科技竞赛项目</h2><p style="text-align:center;">发表于: 2015/4/9 8:43:42</p><p>&nbsp;&nbsp;&nbsp; 日前，浙江省教育厅发文，2015年全省大学生科技竞赛中新增“浙江省大学生化学竞赛”。<br>&nbsp;&nbsp;&nbsp; 为适应新形势下高等教育内涵发展的需要，推动以学科竞赛为载体的第二课堂和第一课堂教学改革的良性互动，扩大竞赛专业覆盖面，有效整合已有竞赛资源，强化竞赛对学生实践能力、工程能力、创新能力的培养，形成以学生为本的国家、省、校三级竞赛体系，促进大学生科技竞赛持续健康发展，经浙江省大学生科技竞赛委会审议，结合专业学科分布和社会需求，我省将在2015年举办28项大学生科技竞赛，其中新增“浙江省大学生化学竞赛”等四项赛事。<br>&nbsp;&nbsp;&nbsp; “浙江省大学生化学竞赛”由原“浙江省高校基础化学实验技能大赛”（2004年我校发起）、“浙江省高校化学学科竞赛”（2012年改名）提升而来，至今已连续举办了六届。该项赛事将化学理论知识与化学实验知识、实验技能、创新能力紧密结合，竞赛既要进行笔试，又要进行实验方案设计、实验操作和研究报告的撰写、答辩，是一个完整的、系统的科学研究过程，充分体现化学学科特色。本次提升为省级赛项，将在竞赛形式、竞赛覆盖面、竞赛时间安排等方面作较大调整，以更加全面地检阅我省大学生化学学科水平，以及大学生个体发展能力与创新潜质，探索培养大学生实践创新能力的新思路和新途径。同时，以赛促建，促进我省大学化学教学改革，提升人才培养质量。<br>&nbsp;&nbsp;&nbsp; “浙江省大学生化学竞赛”秘书处设在浙江工业大学。</p>', NULL, N'', N'2015/4/9 8:43:42', N'1', N'2020-11-20', N'admin', N'9', N'', N'', N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'E793F76C-0A6C-409F-A607-FE8F025A40EE', N'0', N'14', N'', N'化学化工实验教学中心被批准为国家级实验教学示范中心', N'<h2 style="text-align:center;">化学化工实验教学中心被批准为国家级实验教学示范中心</h2><p style="text-align:center;">发表于: 2015/4/8 15:54:48</p><p>&nbsp;&nbsp; &nbsp; 2015年1月8日，教育部发文（教高厅函[2015]2号）批准我校化学化工实验教学中心等全国80个实验教学中心为国家级实验教学示范中心。<br>&nbsp;&nbsp;&nbsp; 我校化学化工实验教学中心在建设和发展过程中，根据学校的办学目标和发展定位，以培养学生实践能力、创新能力和社会责任感为主线，遵循“多元融合、创新主导、寓教于研、协同育人”的建设理念，对化学化工实验教学体系重新进行顶层设计，引入科研发展的课程内容和训练方式，实施多样化的教学方法，促进开放式实验教学，建设了具有地方院校学科优势和特色的创新性人才培养的实验教学平台，形成了有利于化学、化工创新性人才培养的实验教学新体系。主要特色是：<br>&nbsp;&nbsp;&nbsp; 1. 以学生为中心，以实践能力和创新能力培养为重点，多元融合，构建了化学与化工实验一体化教学的新体系。<br>&nbsp;&nbsp;&nbsp; 在注重实验教学的“基础性、层次性、自主性、开放性”基础上，强化“综合性、系统性、创新性、工程性”，实现了从“实验室科学学习—专业技能训练—工程实践锻炼”的知识与能力连贯式教学，学生学习更具系统性、完整性，体现了“实验教学与理论教学相结合、实验内容与化学化工工程实际相结合、基本能力与创新能力培养相结合”的人才培养新要求。<br>&nbsp;&nbsp;&nbsp; 2. 以优势学科为支撑，以服务区域经济和社会发展为导向，培养“下得去、用得上、干得好、上得来”的化学化工人才。<br>&nbsp;&nbsp;&nbsp; 依托学校优质学科科研实验平台，按照学校“前期宽口径按类培养、后期多样化专业教育”的基本框架设计培养方案，实验中心积极拓展课程体系，扩展选修课程，如在工科专业中开设“综合化学实验”、“现代测试技术”等选修课，并通过在“非化”专业中开设“工程训练实验”等选修课程，完善人才知识结构，创新人才培养模式。同时，提高实验教师在本科教育中的地位，充分发挥实验教师的积极作用，组建了专兼职教师相结合的集“实验教学、实验教学研究、工程实践能力培养”于一体的实验教学团队，保证了实验教学持续发展的需求。<br>&nbsp;&nbsp;&nbsp; 教育部在《通知》中要求：地方和军队教育行政部门应进一步加大对所属高校实验教学示范中心体系建设力度，健全配套政策，落实支持措施，积极组织所属高校与其他高校国家级、省级实验教学示范中心开展交流与合作，学习借鉴实验教学中心建设和管理的优秀经验，共同探索实验教学改革新思路、新方法，全面提升实验教学中心建设水平。</p>', NULL, N'', N'2015/4/8 15:54:48', N'1', N'2020-11-20', N'admin', N'10', N'', N'', N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'65A7F622-3E2E-4228-BFC3-691ED004AF0C', N'0', N'15', N'', N'浙大宁波理工学院来实验教学中心访问交流', N'<h2 style="text-align:center;">浙大宁波理工学院来实验教学中心访问交流</h2><p style="text-align:center;">发表于: 2014/11/12 15:49:23</p><p>&nbsp;&nbsp;&nbsp; 11月11日下午，浙江大学宁波理工学院生物与化学工程学院尚龙安副院长等一行六人来化学化工实验教学中心访问交流，副院长艾宁接待了客人，并向客人们介绍了我院实验教学示范中心、虚拟仿真建设等情况。双方就实验教学中心管理、实验教学改革等进行了广泛的交流。<br>&nbsp;&nbsp;&nbsp; 化学化工实验教学中心负责人以及相关教师参加了接待和交流。</p>', NULL, N'', N'2014/11/12 15:49:23', N'1', N'2020-11-20', N'admin', N'11', N'', N'', N'0')
