INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'41BACAA5-0A18-44A8-96AF-82087C16AF04', N'0', N'16', N'', N'南京大学张剑荣教授来我校作实验教学专题报告', N'<h2 style="text-align:center;">南京大学张剑荣教授来我校作实验教学专题报告</h2><p style="text-align:center;">发表于: 2014/10/27 10:22:03</p><p>&nbsp;&nbsp;&nbsp; 10月24日下午，南京大学国家级化学实验教学示范中心主任张剑荣教授应我校化学化工实验教学中心的邀请，在浙江工业大学第28期教学论坛作实验教学专题报告。张教授以“对实验教学改革中若干问题的认识”为题，从“理念和思路”、“体系和内容”、“方法和手段”、“安全与管理”四个方面，提出了现代人才培养对实验教学改革的新要求。特别是在实验教学课程体系和内容建设方面，张教授以“一体化、多层次、开放式”为指导思想，改革课程内容，以南京大学具体的教学内容为例，讲述了“基础性、综合性、研究性”实验的实施情况。在综合性、研究性实验方面，既有化学一级学科范畴内的综合化学实验内容，又有跳出一级学科范畴的内容，如包含化学与生物、化学与环境等交叉内容的实验，另外，开设许多贴近生活、贴近实际的教学内容，以此“激发学生兴趣、训练知识应用、引导知识创造、培养团队协作”。在教学手段和方法现代化方面，张教授报告了“PBL（问题式教学法）”、“MOOC（慕课）”、“FCM（翻转课堂教学模式）”等教学方法在实验教学中的应用。同时，张教授以国内外大量的实验室安全事故为例，指出了实验室建设的新要求、新措施。<br>&nbsp;&nbsp;&nbsp; 报告结束后，与会者就我校实验教学中的相关问题与张教授进行了交流。<br>&nbsp;&nbsp;&nbsp; 张剑荣，1963年生，南京大学教授，博士生导师。1980年-1990年，先后在南京大学获得学士、硕士和博士学位。现任南京大学教师教学发展中心专家委员会委员、南京大学赵世良讲座教授、国家级化学实验教学示范中心主任。曾赴美国、德国、法国、日本、新加坡、台湾地区等考察大学基础教学和学术交流。2001年、2005年和2009年分别获得国家级教学成果二等奖。主要从事生物燃料电池、纳米光电材料及其生物传感应用研究；2010年获得教育部自然科学奖一等奖， 2007年和2008年分别获得江苏省科技进步奖二等奖，发表SCI论文100余篇。</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/zhangjianrongbaogao.JPG" alt=""></figure><p>&nbsp;</p>', NULL, N'', N'2014/10/27 10:22:03', N'1', N'2020-11-20', N'admin', N'12', N'', N'', N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'918CF3DA-6150-43E3-A875-8AB8D3975145', N'0', N'17', N'', N'《新编基础化学实验（II)-有机化学实验 （第二版）》出版', N'<h2 style="text-align:center;">《新编基础化学实验（II)-有机化学实验 （第二版）》出版</h2><p style="text-align:center;">发表于: 2014/9/30 8:19:51</p><p>&nbsp;&nbsp;&nbsp; 《新编基础化学实验（II）-有机化学实验》自2007年出版以来，一直在浙江工业大学化工学院、材料学院、海洋学院、生物与环境学院、药学院等大学二年级的有机化学实验课程教学中使用，国内也有数所高校选用本教材。经过7年来的教学实践检验，教学效果良好。随着有机化学理论与实验技术的发展，原教材出现一些局限性，需要增补一些新的内容以满足现代有机化学实验教学的需要。为此，《新编基础化学实验（II）-有机化学实验（第二版）》于今年9月正式出版。<br>&nbsp;&nbsp;&nbsp; 第二版主要增加了以下三个方面的内容。<br>&nbsp;&nbsp;&nbsp; 1.制备实验方面，增加了某些有代表性的实验，如酸酐的制备、醛的制备、硝化反应、Hofmann降解反应等，力求使基本实验的类型比较齐全，在教学中有更多的选择。目前测定有机化合物结构的仪器日益普及,为适应此情况，编写了核磁共振碳谱等内容。<br>&nbsp;&nbsp;&nbsp; 2.综合实验方面，增加了制备色谱、柱色谱等实用性较强的内容，主要体现在3，5-二苯基异噁唑啉的绿色合成，2-亚氨基噻唑啉类化合物的合成等实验中。<br>&nbsp;&nbsp;&nbsp; 3.研究性实验方面有了较大幅度的增加，共编写了10个研究性实验供有机化学实验教学的后期选做。主要增加了取代苯乙炔的多步合成，离子液体中4，6-二取代氨基-1，3，5-三嗪类衍生物的合成，安息香的绿色催化氧化，水杨醛缩肼基二硫代甲酸苄酯类Schiff碱的合成，水杨酸双酚A酯的合成，吗氯贝胺的合成等研究性实验。<br>&nbsp;&nbsp;&nbsp; 这些实验大都经过反复试做，有些直接出自教师的科研项目，具有前沿性与可靠性，体现对学生创新能力培养的重视。此外，对第一版教材中的一些不当之处作了更正，改写了部分思考题，使教材的文字表述更规范准确。新增加的实验数大约占原书的三分之一，书末仍附带实验教学视频光盘。</p><p>&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/jiaoc2ban.jpg" alt=""></figure>', NULL, N'', N'2014/9/30 8:19:51', N'1', N'2020-11-20', N'admin', N'13', N'', N'', N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'FAD8A16F-7DFC-46C7-9E3A-4C0D12F19FE7', N'0', N'18', N'', N'第六届浙江省大学生化学学科竞赛暨第十一届中心主任联席会在浙江大学举行', N'<h2 style="text-align:center;">第六届浙江省大学生化学学科竞赛暨第十一届中心主任联席会在浙江大学举行</h2><p style="text-align:center;">发表于: 2014/9/30 8:19:18</p><p>&nbsp;&nbsp;&nbsp; 第六届浙江省大学生化学学科竞赛2014年9月21日落幕。本届竞赛由浙江省化学会、浙江省高等学校化学化工制药类教学指导委员会主办，浙江大学化学系、浙江大学城市学院医学院承办，来自全省各高校的36支队伍参赛。我校由林快快、孙伟晔、余云开、章裕超组成的参赛队获一等奖。<br>&nbsp;&nbsp;&nbsp; 本届竞赛的主旨是推进实践教学改革，探索本科生实践能力培养的新思路和新方法，提高化学实验教学总体水平。<br>&nbsp;&nbsp;&nbsp; 竞赛分三个阶段进行。<br>&nbsp;&nbsp;&nbsp; 第一阶段：笔试（2014年5月举行）。参赛学生为化学、化工、医学、药学等专业大三本科生。试题由组委会发给各参赛高校。参赛高校自行打印试卷（务必保密）、组织考试（全省统一进行）和批阅试卷。根据笔试结果，选出至少20人的实验阶段候选人名单，于2014年5月底报组委会。组委会从中随机选出4人，组成参赛团队。<br>&nbsp;&nbsp;&nbsp; 第二阶段：实验（2014年7月1日-15日举行）。竞赛题目提前1-2天发布，参赛高校自行完成。为了体现竞赛的真实性和规范性，各参赛高校代表队实时收集实验设计方案、实验原始记录等资料，实验过程全程录像。所有记录资料及实验结果按时上交。<br>第三阶段：答辩（2014年9月20日举行）。第六届浙江省大学生化学学科竞赛的公开答辩、第十一届浙江省高校化学实验教学中心主任联席会同时在浙江大学举行。<br>&nbsp;&nbsp;&nbsp; 第十一届浙江省高校化学实验教学中心主任联席会围绕高校化学实验体系与创新、实验室管理与安全、实验教学研究及实验课程改革、实验队伍建设等主题开展了深入的研究与探讨。 我校强根荣老师在大会上作了“以‘联席会’和竞赛为载体 构建实验教学改革交流的新平台—浙江省高校化学实验教学中心主任联席会暨化学学科（实验）竞赛十周年回顾”的报告，全面总结了“联席会”和实验竞赛十年来的发展情况以及取得的成效，对今后的工作提出了思路。<br>&nbsp;&nbsp;&nbsp; 本届会议还确定第十二届浙江省高校化学实验教学中心主任联席会由嘉兴学院举办，第七届浙江省大学生化学学科竞赛暨第十三届浙江省高校化学实验教学中心主任联席会由浙江工业大学举办。</p><p>&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/14shengjiang.jpg" alt=""></figure><p>&nbsp;&nbsp;&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/jieqi.jpg" alt=""></figure>', NULL, N'', N'2014/9/30 8:19:18', N'1', N'2020-11-20', N'admin', N'14', N'', N'', N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'6C429BE5-8CB2-47DE-BC41-1DFDA3482EEB', N'0', N'19', N'', N'2014年国家级实验教学示范中心联席会化学化工学科组会议在大连召开', N'<h2 style="text-align:center;">2014年国家级实验教学示范中心联席会化学化工学科组会议在大连召开</h2><p style="text-align:center;">发表于: 2014/8/5 9:23:29</p><p>&nbsp;&nbsp;&nbsp;&nbsp; 2014年8月2日-4日，国家级实验教学示范中心联席会化学化工学科组会议在大连理工大学召开。本次会议的主题是：1.化学化工虚拟仿真实验中心建设；2.化学化工类实验室安全环保的管理与教育；3.化学化工类实验教学示范中心建设经验交流；4.研讨化学类专业标准中化学实验教学的要求。会议上，教育部高等教育司实验室处高东锋副处长、联席会主席、南京大学化学实验教学中心张剑荣教授、华东理工大学化学实验教学中心孙学芹教授、大连理工大学化学虚拟实验中心刘志广教授等专家、学者分别就上述主题作了专题报告。<br>&nbsp;&nbsp;&nbsp; 我校化学化工国家级虚拟仿真实验教学中心强根荣、刘华彦老师参加了本次会议，并就我校化学化工虚拟仿真教学情况、下一步工作思路向与会者作了交流。会议决定在下半年适当时候将组织虚拟仿真教学的专题研讨会。</p>', NULL, N'', N'2014/8/5 9:23:29', N'1', N'2020-11-20', N'admin', N'15', N'', N'', N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'EA3A279A-73CD-4A93-BA86-AE09254C9FB5', N'0', N'20', N'', N'校学生在第九届全国大学生化学实验邀请赛再获佳绩', N'<h2 style="text-align:center;">校学生在第九届全国大学生化学实验邀请赛再获佳绩</h2><p style="text-align:center;">发表于: 2014/7/18 8:03:45</p><p>&nbsp;&nbsp;&nbsp; 7月11日-15日，第九届全国大学生化学实验邀请赛在兰州大学举行，浙江工业大学化工学院三位参赛学生凭借扎实的知识基础和娴熟的实验操作，取得了评审老师的一致认可，其中代文动和占贝贝获国家二等奖，俞纬获国家三等奖，这也是我校参加此项竞赛以来的最好成绩。<br>&nbsp;&nbsp;&nbsp; 此次大赛由教育部高等学校化学教育研究中心主办，兰州大学承办，共有来自包括北京大学、清华大学和浙江大学等在内的全国42所知名高校的126名学生和200余名指导教师参加竞赛和实验教学研讨。大赛分为笔试和实践操作两部分，其中笔试成绩占30%，操作成绩占70%。我校学生能在全国学生中脱颖而出，是我们一直以来重视学生专业知识积累、实验操作规范的成果，也是我校学生一直勤奋踏实学习的风貌展现。<br>&nbsp;&nbsp;&nbsp; 第十届全国大学生化学实验邀请赛将于2016年在南京大学举行。</p><p>&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/zhanbeibei.jpg" alt=""></figure><p>&nbsp;&nbsp;&nbsp;&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/yuwei.jpg" alt=""></figure><p>&nbsp;&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/daiwendong.jpg" alt=""></figure><p>&nbsp;&nbsp;&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/hezhao.JPG" alt=""></figure>', NULL, N'', N'2014/7/18 8:03:45', N'1', N'2020-11-20', N'admin', N'16', N'', N'', N'0')
