INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'2171159B-7968-4961-BF95-C2AD18C2DB0B', N'0', N'21', N'', N'兰州大学化学化工学院来我院参观交流', N'<h2 style="text-align:center;">兰州大学化学化工学院来我院参观交流</h2><p style="text-align:center;">发表于: 2014/7/3 15:17:23</p><p>&nbsp;&nbsp;&nbsp; 7月2日，兰州大学化学化工学院冯庆华等一行来我院就化学化工实验教学中心建设情况进行交流。<br>&nbsp;&nbsp;&nbsp; 副院长艾宁教授接待来访者，并向客人们介绍了学校、学院的基本情况，着重就我校化学化工实验教学中心以及化学化工国家级虚拟仿真实验教学中心建设情况同客人们进行了广泛地交流。<br>&nbsp;&nbsp;&nbsp; 兰州大学化学化工学院一行对我校近年来的快速发展，以及我院实验教学中心所取得的成绩表示赞许，并就实验中心建设理念、建设方案和建设特色等，尤其是化学化工虚拟仿真实验教学中心建设过程中遇到的问题和困难与我校进行了深入探讨。<br>&nbsp;&nbsp;&nbsp; 化学省级实验教学示范中心常务副主任强根荣、化工省级实验教学示范中心副主任杨阿三、姬登祥等老师参加接待与交流，并陪同客人们参观了我校化学实验教学中心实验室、化工原理教学实验室、国家级虚拟仿真实验教学中心实践基地、化工实验教学中心实验室等。</p><p>&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/landacanguan.jpg" alt=""></figure><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/landacanguan2.jpg" alt=""></figure><p>&nbsp;</p>', NULL, N'', N'2014/7/3 15:17:23', N'1', N'2020-11-20', N'admin', N'17', N'', N'', N'0')
