INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'D8B233E2-5E08-4CCB-ACAF-1544C9FC5154', N'0', N'23', N'', N'我校举办第六届基础化学实验大赛我校举办第六届基础化学实验大赛', N'<h2 style="text-align:center;">我校举办第六届基础化学实验大赛</h2><p style="text-align:center;">发表于: 2013/12/14 15:25:54</p><p>&nbsp;&nbsp;&nbsp; 11月9日至12月初，我校第六届基础化学实验大赛在朝晖校区举办。该项竞赛由教务处主办、化材学院和化学省级实验教学示范中心承办，共有来自化材学院、健行学院、生环学院、药学院、绿色制药协同创新中心等5个学院、部门的110多位学生报名参赛。<br>&nbsp;&nbsp;&nbsp; 比赛分为实验理论笔试、实验操作等环节，内容涵盖无机化学、分析化学、有机化学、物理化学等科目。通过实验理论笔试后，选出42人参加实验操作考试，最终共评选出一等奖3人、二等奖6人、三等奖9人。<br>&nbsp;&nbsp;&nbsp; 本届大赛旨在进一步推动学生化学实验基础知识、基本理论、基本技能的提高和实验设计能力的锻炼，培养大学生全面观察问题、分析问题、解决问题的综合能力。大赛客观地反映了我校化学实验教学的现状，有利于促进化学实验教学内容的更新、化学实验教学方法和考试模式的深化改革，推动“基础化学实验”国家精品课程建设的转型升级。</p><p>&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/IMG_6579.jpg" alt=""></figure><p>&nbsp;&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/IMG_6582.jpg" alt=""></figure><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/IMG_6586.jpg" alt=""></figure><p>&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/IMG_6594.jpg" alt=""></figure><p>&nbsp;</p>', NULL, N'', N'2013/12/14 15:25:54', N'1', N'2020-11-20', N'admin', N'21', N'', N'', N'0')
