INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'7F71481A-CFCC-48ED-9451-081365304518', N'0', N'24', N'', N'第十届浙江省高校化学实验教学中心主任联席会在衢州学院召开', N'<h2 style="text-align:center;">第十届浙江省高校化学实验教学中心主任联席会在衢州学院召开</h2><p style="text-align:center;">发表于: 2013/12/2 21:42:48</p><p>&nbsp;&nbsp;&nbsp; 第十屇浙江省高校化学实验教学中心主任联席会于2013年11月30日在衢州学院举行。<br>&nbsp;&nbsp;&nbsp; 本次会议的主题是：（1）如何深化实验教学改革，进行更为有效的实验教学？（2）如何更好地举办浙江省化学学科竞赛？（3）如何更好地举办实验中心主任联席会议？与会代表就上述主题进行了热烈讨论。2014年全省高校化学学科竞赛将在浙江大学举行。<br>&nbsp;&nbsp;&nbsp; 我校化学实验教学中心强根荣老师以“理论与实践贯通的专题式有机化学实验教学改革探索”为题，在会上作了交流报告，受到大家的广泛兴趣。</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/IMG_6459.jpg" alt=""></figure><p>&nbsp;&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/强老师3.JPG" alt=""></figure>', NULL, N'', N'2013/12/2 21:42:48', N'1', N'2020-11-20', N'admin', N'19', N'', N'', N'0')
