INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'AAE923F3-D689-472F-AAFC-834FAA94C11B', N'0', N'25', N'', N'高校化学化工课程教学系列报告会（2013）在扬州举行', N'<h2 style="text-align:center;">高校化学化工课程教学系列报告会（2013）在扬州举行</h2><p style="text-align:center;">发表于: 2013/11/24 14:08:55</p><p>&nbsp;&nbsp;&nbsp; 由全国高等学校教学研究中心、全国高等学校教学研究会、教育部高等学校化学化工类相关教学指导委员会、高等学校化学教育研究中心、中国高等教育学会理科教育专业委员会、高等教育出版社与有关高校共同主办的“大学化学化工课程报告论坛”自2006年创设以来，得到了广大高校、教师的大力支持和积极响应，赢得了广泛赞誉和高度认同。自本届论坛起，“大学化学化工课程报告论坛”更名为“高校化学化工课程教学系列报告会”。<br>&nbsp;&nbsp;&nbsp; 2013年高校化学化工课程教学报告会以“精品开放课程建设与共享”为主题，围绕国家精品资源共享课建设中的三大重点——转型升级、协同创新、共建共享，为校、省、国家三级精品资源共享课建设搭建交流、研讨、提高的平台。<br>&nbsp;&nbsp;&nbsp; 2013年报告会于11月22—24日在扬州召开，由扬州大学承办。报告会邀请了国内知名专家、教育部高等学校化学化工类相关教学指导委员会委员、国家精品资源共享课负责人等作主题报告，并以分组报告、分组讨论和书面交流等形式展开了充分研讨。<br>&nbsp;&nbsp;&nbsp; 我校化学实验教学中心强根荣老师在实验教学分会场作了题为“基础化学实验国家精品课程转型升级的思考与实践”的报告，向与会代表介绍了我校基础化学实验国家精品课程的建设情况、基础化学实验精品课程转型升级的实践过程以及对未来建设的展望。我校在基础化学实验精品资源开放、共享方面所作的工作和贡献，引起大家的广泛关注和赞赏。</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/1(2).JPG" alt=""></figure><p>&nbsp;&nbsp;&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/2.JPG" alt=""></figure><p>&nbsp;</p>', NULL, N'', N' 2013/11/24 14:08:55', N'1', N'2020-11-20', N'admin', N'20', N'', N'', N'0')
GO

