INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'F8F33654-5694-4208-B196-37C289643663', N'0', N'28', N'', N'化学实验教学中心教工党支部获第八轮支部建设创新评比二等奖', N'<h2 style="margin-left:0px;text-align:center;"><strong>化学实验教学中心教工党支部获第八轮支部建设创新评比二等奖</strong></h2><p style="text-align:center;">发表于: 2013/4/8 12:16:31</p><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp; 日前，化学工程与材料学院党委表彰了第八轮支部建设创新活动先进支部，化学实验教学中心教工党支部获二等奖。<br>&nbsp;&nbsp;&nbsp; 化学实验教学中心党支部现有党员9人，占中心教师队伍80%以上。在2012年中，化学实验教学中心党支部通过电视、网络等媒体，强化基本理论知识以及“党的十八大”等重要会议精神学习，注重支部队伍建设的同时，不断总结实验室建设以及课程教学经验，紧紧围绕文化建设这一主题，组织党员积极开展一系列活动，取得了较好的效果。在实验室建设、课程建设和实验教学过程中，党员教师发挥先锋模范作用，经过不断的建设和发展，承担的“基础化学实验”课程已建设成为国家级精品课程，实验教学中心在省级实验教学示范中心的基础上，已被学校推荐申报国家级实验教学示范中心，目前申报工作进展顺利。</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/%E6%9C%AA%E5%91%BD%E5%90%8D(3).JPG" alt=""></figure><p>&nbsp;</p>', NULL, N'', N'2013/4/8 12:16:31', N'1', N'2020-11-20', N'admin', N'24', N'', N'', N'0')
