INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'AF82DA09-E93A-43D7-9104-97D888601F04', N'0', N'29', N'', N'化学实验教学中心获2011-2012年度本科教学业绩考核奖', N'<h2 style="margin-left:0px;text-align:center;"><strong>化学实验教学中心获2011-2012年度本科教学业绩考核奖</strong></h2><p style="text-align:center;">发表于: 2013/4/8 11:45:47</p><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp; 化学工程与材料学院2012年度分项工作考核评优日前结束，化学实验教学中心获本科教学业绩考核鼓励奖。<br>&nbsp;&nbsp;&nbsp; 本年度学院分项工作分为“人才培养工作”、“科研工作”、“精神文明与学科建设”、“本科教学业绩考核”、“目标责任制专项工作”五个方面。全院8个教学系（实验中心）参加了“本科教学业绩考核”的分项评优，评出“优秀奖”和“鼓励奖”各1名。<br>&nbsp;&nbsp;&nbsp; 2012年化学实验教学中心对学院的主要贡献是：<br>&nbsp;&nbsp;&nbsp; 1. 学校教学成果一等奖<br>&nbsp;&nbsp;&nbsp; 2. 国家精品课程申报国家精品资源共享课程<br>&nbsp;&nbsp;&nbsp; 3. 国家级实验教学示范中心申报<br>&nbsp;&nbsp;&nbsp; 4. 扩大教改成果辐射面：全国大学化学实验教学研讨会（复旦大学）、第七届全国“大学化学化工课程报告论坛”（杭州）、全省高校化学实验教学研讨会（温州大学）作交流报告。</p><p style="margin-left:0px;">&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/P1010011(2).JPG" alt=""></figure><p>&nbsp;</p>', NULL, N'', N'2013/4/8 11:45:47', N'1', N'2020-11-20', N'admin', N'25', N'', N'', N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'902F9A7D-B72F-4361-A350-8844B207CD5F', N'0', N'30', N'', N'化学实验教学中心获学校教学成果奖一等奖', N'<h2 style="margin-left:0px;text-align:center;"><strong>化学实验教学中心获学校教学成果奖一等奖</strong></h2><p>发表于: 2012/12/25 12:29:39</p><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp; 在2012年12月20 -21日学校第八届教育工作会议上，表彰了49项校级教学成果奖，其中一等奖20项、二等奖29项。化学实验教学中心强根荣等申报的“融创新教育于基础教学的实验教学体系与平台的构建”成果获一等奖。<br>&nbsp;&nbsp;&nbsp; 该项成果以化学实验教学改革为突破口，凝练、深化了“基础化学实验”国家精品课程建设的重要内容，研究、探讨了在基础教学中实施创新能力培养的方式、方法，优化、重构了有利于学生创新能力提高的实验教学体系，集成打造了软硬件一体的教学新平台，拓展了“学、研”结合、多重交互的学习空间。成果对“化学工程与工艺、应用化学、生物工程、制药工程”等国家级特色专业的建设起基础支撑作用，对其它课程和专业建设具有普遍的适用性。<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;（1）开放式的创新教学体系。构建了从“基础规范性实验—综合设计性实验―研究探索性实验”到“创新训练”的“三层次、一创新、递进式”实验教学新体系，将“挑战杯”、“大学生科技创新项目”等课外科技活动融入实验教学，一方面将创新教育渗透到了基础教学的各个层面，另一方面将创新教育从课内延伸到了课外，拧紧了“创新教育”与“基础教学”的纽带。<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;（2）先进的教学内容和创新平台。研究开发了一批有利于创新实践能力培养的教学内容，出版了相应的《综合化学实验》教材。统筹布局开放式的综合化学实验室和学生自主创新实验室，创建了化学实验实训导训系统，实现了远程式的多媒体网络化教学。<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;（3）多功能的开放管理新机制。扩展了实验室功能，实验室由简单的教学管理场所变为“实验教学与改革研究相结合”的基地，由单一的基础教学场所变为“融基本技能提高与创新能力培养于一体”的基地，由纯粹的培养学生场所变为“对外开放与师资培训相结合”的基地。</p><p style="margin-left:0px;">&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/%E6%B5%99%E6%B1%9F%E5%B7%A5%E4%B8%9A%E5%A4%A7%E5%AD%A6%E6%95%99%E5%AD%A6%E6%88%90%E6%9E%9C%E5%A5%96%E8%8E%B7%E5%A5%96%E8%AF%81%E4%B9%A62.JPG" alt=""></figure><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/201212250947423.jpg" alt=""></figure><p>&nbsp;</p>', NULL, N'', N'2012/12/25 12:29:39', N'1', N'2020-11-20', N'admin', N'26', N'', N'', N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'5229F691-E6F3-4A0F-93E9-8A4A8E0169E4', N'0', N'31', N'', N'中心举行教研活动—美国密西西比大学普化实验室考察报告', N'<h2 style="margin-left:0px;text-align:center;"><strong>中心举行教研活动—美国密西西比大学普化实验室考察报告</strong></h2><p style="text-align:center;">发表于: 2012/11/16 9:02:37</p><p style="margin-left:0px;">&nbsp;&nbsp; &nbsp;2012年9月12日，化学实验教学中心举行教研活动，在通报了近期的一些教学情况后，范永仙老师作了对美国密西西比大学普通化学实验室考察情况的报告。<br>&nbsp;&nbsp;&nbsp;&nbsp;密西西比大学成立于1848年。该校的药科学院在美国卫生研究院出资成立的药科学院中排名第20位，同时也是国家投资数额第二位的药科学校，是密西西比州最古老的大学，目前在校学生规模2万多人。<br>&nbsp;&nbsp;&nbsp; 在报告中，范老师首先介绍了密西西比大学的基本概况，详细介绍了密西西比大学普通化学实验教学内容、教学方法、教学要求、实验考核评分规则以及实验室管理等情况。有几点值得关注的是：（1）在教学内容方面，密西西比大学开设了有关的物质结构实验，普通化学实验总共开设了10个实验；（2）在教学方式上，该校也采用教授讲课、TA 辅助指导的方法，实验前由教授集中讲解，然后分班实验；（3）实验考核方面，学期结束时采用笔试的方式，考察学生对实验理论的掌握情况，实验缺席者一律作不及格论处；（4）实验室安全方面，该校采用安全知识考核准入制度，实验室的水电气设施齐全，气体通过管道集中供应。（5）在实验室管理方面，该校非常严格，学生打破仪器按原价100%赔偿，实验结束后仪器洗干净送回准备室，另外，也安排值日生，其工作状况计入实验成绩。<br>&nbsp;&nbsp;&nbsp;&nbsp;报告结束后，与会老师针对我们现行的实验教学情况展开了讨论，并对今后工作提出了新的思路和要求，活动取得了良好的效果。</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/IMG_17131.JPG" alt=""></figure><p>&nbsp;</p>', NULL, N'', N'2012/11/16 9:02:37', N'1', N'2020-11-20', N'admin', N'27', N'', N'', N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'83C30455-13DB-46EC-93F5-71CA141EF9BB', N'0', N'32', N'', N'化学实验教学中心在第七届“大学化学化工课程报告论坛”作专题报告', N'<h2 style="margin-left:0px;text-align:center;"><strong>化学实验教学中心在第七届“大学化学化工课程报告论坛”作专题报告</strong></h2><p style="text-align:center;">发表于: 2012/10/26 12:51:26</p><figure class="image image-style-align-left"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/q_079_0246.JPG" alt=""></figure><h3 style="margin-left:0px;">&nbsp; 由我校承办的第七届“大学化学化工课程报告论坛”于2012年10月20日－21日在杭州宝盛水博园大酒店举行。本届课程报告论坛由全国高等学校教学研究中心、全国高等学校教学研究会、教育部高等学校化学与化工学科教学指导委员会、高等学校化学教育研究中心、中国高等教育学会理科教育专业委员会、高等教育出版社与有关高校共同主办。<br>&nbsp;&nbsp;&nbsp; 本届课程报告论坛以“国家精品课程转型升级的认识与实践”为主题，通过专家主题报告、专题报告等形式开展交流研讨，主要专题有化学化工国家精品课程建设的认识与实践、化学化工国家精品课程建设与精品开放课程建设、化学化工精品开放课程建设与课程教学方法、教学内容改革、化学化工精品开放课程建设与共享等。<br>&nbsp;&nbsp;&nbsp; 化学实验教学中心常务副主任强根荣老师，在“普通与无机化学”分会场作专题报告，向与会代表介绍了我校“基础化学实验”国家精品课程建设情况及所取得的成效。</h3><p>&nbsp;</p>', NULL, N'', N'2012/10/26 12:51:26', N'1', N'2020-11-20', N'admin', N'28', N'', N'', N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'30C9F882-24E9-4F10-87E0-2C45CBA479FF', N'0', N'33', N'', N'我校学生在“俊尔”杯第五届浙江省大学生化学学科竞赛中喜获佳绩', N'<h2 style="margin-left:0px;text-align:center;"><strong>我校学生在“俊尔”杯第五届浙江省大学生化学学科竞赛中喜获佳绩</strong></h2><p style="text-align:center;">发表于: 2012/10/8 13:23:54</p><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp; &nbsp;10月5日-7日，2012年“俊尔”杯第五届浙江省大学生化学学科竞赛在温州大学举行第二阶段的汇报与答辩。本届竞赛共有来自全省高校的24支代表队参加，我校参赛代表队由化材学院王杰、张柳军和健行学院蓝舒琴、吴江四位同学组成。经过紧张激烈的角逐，我校和浙江大学等四所高校喜获一等奖。<br>&nbsp;&nbsp;&nbsp; 全省大学生化学学科竞赛由浙江省化学会、浙江省高校化学化工及制药类专业教学指导委员会主办。本届竞赛是在前四届全省高校基础化学实验技能大赛的基础上经改革创新而举行的，竞赛内容为工业废弃物中相关组成成分的分析与测试、原料的提取与纯化、工业产品的制备和表征等，实验过程需全程录像和上传。竞赛在规定的时间内分两个阶段进行，7月份进行了第一阶段的综合性实验过程，并提交了原料基本组成分析测试报告、产品检测报告、实验研究报告等。<br>&nbsp;&nbsp;&nbsp; 全省大学生化学学科竞赛有效地深化了原有的实验技能大赛，旨在促进我省高校化学及相关专业实验教学水平的提高，培养学生的创新能力，提高学生的实践能力和综合素质。</p><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/P1010011.JPG" alt=""></figure><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp;&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/P1010015.JPG" alt=""></figure><p>&nbsp;</p>', NULL, N'', N'2012/10/8 13:23:54', N'1', N'2020-11-20', N'admin', N'29', N'', N'', N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'206601B7-1932-4D96-956E-76EF97E73275', N'0', N'34', N'', N'化学实验教学中心作化学实验安全教育', N'<h2 style="margin-left:0px;text-align:center;"><strong>化学实验教学中心作化学实验安全教育</strong></h2><p>发表于: 2012/9/26 12:00:04</p><p style="margin-left:0px;">&nbsp;&nbsp; 2012年9月24日，省级化学实验教学示范中心常务副主任强根荣老师在屏峰校区作了题为“化学实验规则与实验室安全”的报告，对化材学院2012级200多位学生进行了化学实验安全教育。<br>&nbsp;&nbsp;&nbsp; 化学实验中经常要用到易燃易爆、有毒有害的物质，实验室中大大小小的事故也经常发生。因此，对每一个进行化学实验的人来说，良好的实验习惯和强烈的安全意识是非常重要的。强老师首先向学生们介绍了大学期间基础化学实验的教学安排，接着从“化学实验规则”、“化学实验室安全”等方面对学生提出了要求，并讲解了如何更加有效地预防事故的发生及事故发生后采取的措施。最后，他鼓舞大家“化学虽然比较集中地与较多危险的物质打交道，但绝对不能把‘化学’和‘危险’等同起来，只要大家都按规则办事，就是安全的。只要大家都具备基本的化学素养，就能共同有效地维护我们工作和生活环境的安全。”<br>&nbsp;&nbsp;&nbsp; 报告结束后，播放了有关安全教育的录像片。</p><p style="margin-left:0px;"><br>&nbsp;&nbsp;&nbsp;&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/%E6%9C%AA%E5%91%BD%E5%90%8D(1).bmp" alt=""></figure><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp;&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/DSC03100.JPG" alt=""></figure><p>&nbsp;</p>', NULL, N'', N'2012/9/26 12:00:04', N'1', N'2020-11-20', N'admin', N'30', N'', N'', N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'CCD7EC69-A906-4446-802B-ACD1B42D1574', N'0', N'35', N'', N'强根荣在全国大学化学实验教学研讨会上作交流报告', N'<h2 style="margin-left:0px;text-align:center;"><strong>强根荣在全国大学化学实验教学研讨会上作交流报告</strong></h2><p style="text-align:center;">发表于: 2012/7/7 9:50:12</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/DSC06037(1).JPG" alt=""></figure><p style="margin-left:0px;">&nbsp; 7月1日-5日，第八届全国大学生化学实验邀请赛在复旦大学顺利举行。邀请赛期间举办了“全国大学化学实验教学研讨会”，分“实验教学综合管理”和“基础实验课教学”两个分会场，大家围绕实验教学研究和改革、学生实践能力和创新意识培养、实验教学队伍建设和管理等议题进行了研讨和交流。<br>&nbsp;&nbsp;&nbsp;&nbsp;我校化学实验教学中心强根荣老师应邀作了题为“融创新能力培养于基础教学的化学实验教学”的交流报告，从“基础教学中创新能力培养的教学体系”、“基础教学中创新能力培养的教学方式”、“基础教学中创新能力培养的教学平台”、“基础教学中创新能力培养的教学内容”四个方面向与会者介绍了我校化学实验教学研究、改革及创新实践过程。</p><p>&nbsp;</p>', NULL, N'', N'2012/7/7 9:50:12', N'1', N'2020-11-20', N'admin', N'31', N'', N'', N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'CCCDB9F4-B4BE-4125-8120-4DE934908096', N'0', N'36', N'', N'我校学生在第八届全国大学生化学实验邀请赛中取得佳绩', N'<h2 style="margin-left:0px;text-align:center;"><strong>我校学生在第八届全国大学生化学实验邀请赛中取得佳绩</strong></h2><p style="text-align:center;">发表于: 2012/7/6 15:12:23</p><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp; 7月1-5日，第八届全国大学生化学实验邀请赛在复旦大学顺利举行。我校化材学院来霞菲获二等奖，化材学院李飞芳、鲍海波分别获得三等奖，我校获奖级别及数量名列参赛高校前三分之一。<br>&nbsp;&nbsp;&nbsp; 全国大学生化学实验邀请赛由教育部高等学校化学教育研究中心主办，是我国高校化学教学界最高级别的赛事，同时也是国家自然科学基金委资助的中国高等院校化学实验教学的交流盛会。邀请赛旨在检验我国高等学校化学实验教学改革的成果，加强交流，总结经验，推动化学实验教学模式、教学内容、教学方法的改革，探索培养创新型化学人才的思路、途径和方法。邀请赛每两年举办一次，自1998年以来，已先后在南开大学、吉林大学、北京大学、厦门大学、中山大学、浙江大学、武汉大学成功地举办了七届。此项赛事对提高我国高校本科化学实验教学总体水平、推进大学化学实验教学改革发挥了重要作用，在全国产生了深远的影响。<br>&nbsp;&nbsp;&nbsp; 本届全国大学生化学实验邀请赛在复旦大学举行，有北京大学、清华大学、南京大学、浙江大学、复旦大学、厦门大学等42所具有理科或工科化学基地及部分国家级化学实验教学示范中心的知名高校参赛。本届邀请赛延续了由参赛学校提供当年该校化学类专业大三学生总人数的30%名单，由组委会随机抽出3名选手作为该校参赛选手，在赛前2周通知参赛学校的选手产生方式。在临考前由组委会决定选手参加无机化学实验、有机化学实验、物理化学实验中的一门课程的比赛，以避免参赛学校为在比赛中获得好成绩而集中少数学生进行过度强化培训的弊端，提高了邀请赛的客观性、公正性，促进了学生化学实验知识与技能的全面提高。<br>&nbsp;&nbsp;&nbsp; 竞赛内容由实验理论笔试和实验操作考试两个部分组成，选手的成绩由笔试成绩（30%）和实验操作成绩（70%）组成。第一届至第七届邀请赛各类奖项的比例为100%。本届邀请赛，选手的获奖面调整为参赛选手总数的60%，其中一等奖为10%，二等奖为20%，三等奖为30%。</p><p style="margin-left:0px;">&nbsp;&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/IMG_4976.jpg" alt=""></figure><p style="margin-left:0px;">&nbsp;&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/IMG_4980.jpg" alt=""></figure><p style="margin-left:0px;">&nbsp;&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/IMG_4982.jpg" alt=""></figure><p style="margin-left:0px;">&nbsp;&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/IMG_4989(1).jpg" alt=""></figure><p>&nbsp;</p>', NULL, N'', N'2012/7/6 15:12:23', N'1', N'2020-11-20', N'admin', N'32', N'', N'', N'0')
