INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'88EA59AB-8473-4203-914B-311468C161CD', N'0', N'37', N'', N'化学实验教学中心党支部开展党日活动', N'<h2 style="margin-left:0px;text-align:center;"><strong>化学实验教学中心党支部开展党日活动</strong></h2><p>发表于: 2012/4/5 7:34:02</p><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp; 2012年3月24日，化学实验教学中心党支部开展党日活动，主题是“化学实验知识与操作技能辅导”，目的是为今年参加硕士研究生入学考试已达到复试分数线的学生，对基础化学实验进行一次全面的复习、巩固、提高，有30多名学生自主报名参加了辅导。<br>&nbsp;&nbsp;&nbsp;&nbsp;化学实验操作技能是化学、化工类本科生必须具备的最基本技能，扎实的实验技既是一个化工类本科毕业生基本素养的反映，也是我院培养卓越工程师的基本要求。根据以往研究生复试过程中暴露的相关问题，化学实验教学中心党支部精心准备，克服了自身繁重的教学工作的矛盾，利用节假日休息时间为全院参加研究生复试的学生有针对性地开展了本次辅导活动。这也是化学实验教学中心党支部一项传统的党日活动。通过活动，进一步提高了学生对实验操作规范性的认识，强化了实验的技能和技巧，也弥补了学生在学习过程中所遗漏的一些问题，取得了良好的效果，也为学生进一步深造打下了良好的基础。</p><p style="margin-left:0px;">&nbsp;</p><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/P1010044(2).JPG" alt=""></figure><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/IMG_1027(2).JPG" alt=""></figure><p>&nbsp;</p>', NULL, N'', N'2012/4/5 7:34:02', N'1', N'2020-11-20', N'admin', N'33', N'', N'', N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'17083AE9-31A8-43C8-81F2-754207378F39', N'0', N'38', N'', N'“基础化学实验”精品课程建设以“优秀”成绩通过省教育厅验收', N'<h2 style="margin-left:0px;text-align:center;"><strong>“基础化学实验”精品课程建设以“优秀”成绩通过省教育厅验收</strong></h2><p style="text-align:center;">发表于: 2012/3/9 9:24:48</p><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp; 2011年12月，我校“基础化学实验”精品课程建设以“优秀”成绩通过省教育厅验收。<br>&nbsp;&nbsp;&nbsp; 根据《浙江省教育厅关于调整有关教学改革与建设项目评审办法的通知》（浙教高教〔2009〕56号）要求，2009年度省级精品课程建设项目的立项采取“推荐+备案”的办法组织实施。根据通知要求，建设期满两年以后省教育厅统一组织检查验收，经专家验收合格的省级精品课程建设项目确定为省级精品课程。经专家验收评定，拟确定我校的“基础化学实验”等198门课程为省级精品课程。</p><p>&nbsp;</p>', NULL, N'', N'2012/3/9 9:24:48', N'1', N'2020-11-20', N'admin', N'34', N'', N'', N'0')
