INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'FBEFE940-9562-4A76-8ECB-09B0322C373B', N'0', N'39', N'', N'基础化学实验实训导训系统投入使用', N'<h2 style="margin-left:0px;text-align:center;"><strong>基础化学实验实训导训系统投入使用</strong></h2><p style="text-align:center;">发表于: 2012/3/9 8:55:51</p><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp; 由学校投资建设的“基础化学实验实训导训系统”已于本学期正式投入使用。项目的第一期建设在朝晖校区化学实验教学中心的存中楼四层、五层，目前，实验中心正在积极完善、筹备申报项目的第二期，将在化学实验教学中心屏峰校区实验室及存中楼的三楼实验室进行建设。<br>&nbsp;&nbsp;&nbsp; 目前已建成的“基础化学实验实训导训系统”整体由8间实验室（其中2间为导训示范实验室）、1间导训控制中心构成。所有实验室、总控室进行统一管理，在总控室既能观察导训示范实验室情况，又能进行远程控制和导训。各个实验室本身可以根据自身的实际情况做现 场调节，并独立使用。导训示范实验室的视频服务器将智能球机、拾音器采集到的视频图像和声音转换为数字信号，课堂直录播系统软件将教师讲课的视频画面和多媒体课件同时录制下来并进行网络直播，直播图像资料将实时存储在服务器磁盘中，以供任何时间在内部网络进行播看。多媒体导训综合控制平台可以对导训示范实验室的录播过程进行远程控制并能指定直播到那个实训实验室。</p><p>&nbsp;</p>', NULL, N'', N'2012/3/9 8:55:51', N'1', N'2020-11-20', N'admin', N'35', N'', N'', N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'051BF063-C291-40D8-8306-600E62C0E8EF', N'0', N'40', N'', N'强根荣同志被评为2011年度“平安工大”建设、治安综合治理先进个人', N'<h2 style="margin-left:0px;text-align:center;"><strong>强根荣同志被评为2011年度“平安工大”建设、治安综合治理先进个人</strong></h2><p style="text-align:center;">发表于: 2012/3/9 8:34:21</p><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp; &nbsp;2012年3月7日，学校召开“平安工大”建设及治安综合治理工作会议，会议总结了2011年综合治理工作，化材学院等22个单位被评为2011年度 “平安工大”建设、治安综合治理先进单位，化学实验教学中心强根荣等41名同志被评为先进个人。 会上学校与55个责任单位签订了2012年“平安工大”建设、治安综合治理目标管理责任书。校党委副书记何智蕴出席会议并宣读表彰决定。纪委书记卢章陆在会上讲话。<br>&nbsp;&nbsp;&nbsp;&nbsp;卢章陆在讲话中代表学校综治委对2011年的工作进行了总结，并根据当前国内外形势和学校发展特点对2012年的工作提出了五点要求：一是“坚持秩序化的管理方向”，健全制度，规范管理，确保校园治安、交通、周边等安全秩序有条不紊。二是“树立人性化的管理理念”，强化“以服务促平安”的管理意识，并通过普及完善安全教育帮助师生进一步增强法制观念与安全防范意识。三是“建立社会化的安全管理体制”，积极推进校园分片网格化安全管理和大联防安全管理模式，协调政府职能部门、公安部门等社会力量，共同参与开放式校园的安全管理。四是“采取现代化的管理模式”，构建完善“人防、技防、物防、网络防、信息防”五位一体的防范体系，不断提高安全防范的能力。五是“实施精细化的管理方法”，在治安综合治理工作中要进一步细化考核细则、预警机制、安全管理制度以及重点要害部位的管理，确保校园安全稳定。</p><p>&nbsp;</p>', NULL, N'', N'2012/3/9 8:34:21', N'1', N'2020-11-20', N'admin', N'36', N'', N'', N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'E834A83B-EC21-4DDE-9E88-9C1B8A5F8167', N'0', N'41', N'', N'杭州师范大学附属中学来实验中心座谈交流', N'<h2 style="margin-left:0px;text-align:center;"><strong>杭州师范大学附属中学来实验中心座谈交流</strong></h2><p style="text-align:center;">发表于: 2011/12/31 17:04:32</p><p style="margin-left:0px;">2011年12月29日，浙江省一级重点中学、杭州师范大学附属中学化学教研室一行十五人来化学实验教学中心，就当前中学化学实验教学的改革，以及如何做好与大学化学实验教学更好的衔接等问题进行了广泛深入地座谈交流。</p><p style="margin-left:0px;">化材学院高建荣院长接待了来访者，艾宁副院长向客人们介绍了化材学院的情况。</p><p style="margin-left:0px;">化学实验教学中心的老师们结合化学学科的发展以及平时教学过程中的有关问题，指出了当前中学化学实验教学的不足，主要是：1.学生的实验素养不够；2.学生的自我保护意识薄弱；3.学生缺乏基本的安全知识及应对事故的措施；4.学生缺乏实事求是的态度；5.缺乏对学生观察能力的培养。</p><p style="margin-left:0px;">各位老师就如何做好中学化学实验教学与高校化学实验教学的衔接谈了许多建议，希望：1.充分认识化学实验在化学教学中的重要性；2.养成良好的实验习惯，提升学生的实验素养；3.在化学实验中渗透环保教育；4.在学生中普及安全知识，强化学生的自我保护意识；5.倡导学生尊重实验事实，坚持实事求是；6.注意培养学生的观察能力；7.要有严格的标准和规范的操作；8.<span style="color:rgb(43,43,43);">重视化学实验的实验记录和实验报告；</span>9.完善实验操作考试制度。</p><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp; 座谈结束后，客人们参观了化学实验教学中心各实验室。</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/DSC02981.JPG" alt=""></figure><p style="margin-left:0px;">&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/DSC02987.JPG" alt=""></figure><p style="margin-left:0px;">&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/DSC02997.JPG" alt=""></figure><p>&nbsp;</p>', NULL, N'', N'2011/12/31 17:04:32', N'1', N'2020-11-20', N'admin', N'37', N'', N'', N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'C7D4604D-A820-4624-ADC7-DF1EDE3E5A5C', N'0', N'42', N'', N'化学实验教学中心获浙江省高校实验室工作研究成果奖一等奖', N'<h2 style="margin-left:0px;text-align:center;"><strong>化学实验教学中心获浙江省高校实验室工作研究成果奖一等奖</strong></h2><p>发表于: 2011/12/20 11:31:56</p><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp; 日前，由化学实验教学中心完成的“基于学生创新能力培养的普通高校实验室开放模式与管理机制”成果获2010-2011年浙江省高校实验室工作研究成果奖一等奖。<br>&nbsp;&nbsp;&nbsp; 项目组成员在两年的研究过程中，对实验室开放运行机制、开放内容以及开放平台建设等进行了系统的研究，成功地探索了一种适合普通高校实验室开放的模式与管理机制，并将此成果应用于学生综合创新能力的培养，取得了良好的效益。<br>&nbsp;&nbsp;&nbsp; 项目的主要成果有：（1）分层次开放教学新模式。突破了原有的实验教学理念与模式，坚持实验教学改革与创新，实施“教学项目研究化，科研项目教学化”的新机制，建立了“三层次（基础规范性实验—综合设计性实验―研究探索性实验）、一创新（创新训练）”的化学实验教学新体系和相应的实验室分层次开放管理新模式，为创新人才培养提供了良好的平台。（2）多功能开放管理新机制。建立了责任明晰的管理运行机制，积极稳妥地推进全方位开放实验室，在时间和空间上给予了学生最大的自主学习条件。实验室由单纯的教学管理变为实验教学与改革研究相结合的中心，由单一的教学功能变为对外开放与师资培训相结合的基地。（3）先进的开放内容和开放平台。研究开发了一批适合实验室开放和提高学生创新实践能力的教学内容，编写了相应的实验教材。建立了独立的综合化学实验室和学生自主创新实验室，创建了基础化学实验实训导训系统，实现了多媒体网络化教学，为实验室开放创建了良好的平台，使实验室开放真正落到了实处。<br>&nbsp;&nbsp;&nbsp; 成果的创新点和特色是: （1）在“三层次”开放模式的基础上，突出了在“创新意识、创新思维和创新能力”三个不同层面上对学生进行创新教育，稳步有序地推进实验室开放。（2）研究开发的教学内容新颖性和实用性强，注重工程实践能力的提高，架起了实验教学与工程实际之间的桥梁，适合普通高校工科类专业理学课程建设的需要。（3）新建的开放式实验实训导训系统，在总控室既能观察导训示范实验室情况，又能进行远程控制和导训，课堂直录播系统软件将教师讲课的视频画面和多媒体课件同时录制下来，并进行网络直播，为各高校实施实验室开放提供了示范。<br>&nbsp;&nbsp;&nbsp; 该研究成果在实验室开放的模式与管理机制、实验室开放的内容、开放平台的建设等方面，均达到国内同类高校的先进水平。</p><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/%E8%8E%B7%E5%A5%96%E8%AF%81%E4%B9%A6.jpg" alt=""></figure><p>&nbsp;</p>', NULL, N'', N'2011/12/20 11:31:56', N'1', N'2020-11-20', N'admin', N'38', N'', N'', N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'8B6134E1-3EB3-4B5B-9EFB-28098D5209D7', N'0', N'43', N'', N'化材学院开展2011年“实验室安全教育月”活动', N'<h2 style="margin-left:0px;text-align:center;"><strong>化材学院开展2011年“实验室安全教育月”活动</strong></h2><p>发表于: 2011/11/2 13:57:11</p><p style="margin-left:0px;">各学科、实验室：</p><p style="margin-left:0px;">为进一步加强全院师生实验安全意识、增强相关教师和学生的安全防范和处置技能和规范实验室管理，保护师生员工的人身和学校财产安全，确保教学、科研等活动安全有序开展，经院务会研究决定，在2011年11月1日——11月30日开展“安全教育月”活动，具体事项安排见附件，望各学科认真组织有关教师和学生参加。</p><p style="margin-left:0px;text-align:center;"><strong>&nbsp;</strong></p><p style="margin-left:0px;text-align:center;"><strong>&nbsp;</strong></p><p style="margin-left:0px;text-align:center;"><strong>&nbsp;</strong></p><p style="margin-left:0px;text-align:right;"><strong>化学工程与材料学院</strong></p><p style="margin-left:0px;text-align:right;"><strong>2011年11月1日</strong></p><p style="margin-left:0px;"><br>&nbsp;</p><p style="margin-left:0px;">附件一</p><p style="margin-left:0px;text-align:center;">化学工程与材料学院</p><p style="margin-left:0px;text-align:center;">2011年“实验室安全教育月”活动安排</p><p style="margin-left:0px;"><strong>&nbsp;</strong></p><p style="margin-left:0px;"><strong>一、活动主题：</strong>强化安全管理，共建和谐化材</p><p style="margin-left:0px;"><strong>二、指导单位：</strong>学校实验室资产管理处、保卫处</p><p style="margin-left:0px;"><strong>三、参加对象：</strong>2011年新引进教师、2008级本科生、2010级研究生、博士生</p><p style="margin-left:0px;"><strong>四、活动时间：</strong>11月1日—11月30日</p><p style="margin-left:0px;"><strong>五、活动进程安排</strong></p><figure class="table"><table><tbody><tr><td><p style="margin-left:0cm;text-align:center;"><span style="color:black;"><strong>序号</strong></span></p></td><td><p style="margin-left:0cm;text-align:center;"><span style="color:black;"><strong>时间</strong></span></p></td><td><p style="margin-left:0cm;text-align:center;"><span style="color:black;"><strong>内容</strong></span></p><