INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'7340C5F5-F1E2-4CEF-B289-306AE748FA13', N'0', N'45', N'', N'华南理工大学化学与化工学院来访', N'<h2 style="margin-left:0px;text-align:center;"><strong>华南理工大学化学与化工学院来访</strong></h2><p style="text-align:center;">发表于: 2011/4/14 9:37:23</p><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp; 2011年3月22日-23日，华南理工大学化学与化工学院副院长王秀军一行五人来我院参观交流，学院院长高建荣、副院长艾宁接待了来访客人。<br>&nbsp;&nbsp;&nbsp; 化学实验教学中心常务副主任强根荣、副主任刘秋平参加了接待，并向客人们汇报了我校省级化学实验教学示范中心、基础化学实验国家精品课程的建设情况，大家就基础化学实验课程建设、实验室建设、教材建设、实验教学师资队伍建设等共同关心的问题进行了广泛地探讨，并期待着下次再作深入交流。</p><p style="margin-left:0px;"><br>&nbsp;&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/DSC07281-xiu.jpg" alt=""></figure><p style="margin-left:0px;">&nbsp;&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/DSC07282-xiu.jpg" alt=""></figure><p>&nbsp;</p>', NULL, N'', N'2011/4/14 9:37:23', N'1', N'2020-11-20', N'admin', N'41', N'', N'', N'0')
