INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'70DBFCD3-A60A-4C98-8CD7-EDC8F5CCF802', N'0', N'46', N'', N'化学实验教学中心续建方案通过专家论证', N'<h2 style="margin-left:0px;text-align:center;"><strong>化学实验教学中心续建方案通过专家论证</strong></h2><p>发表于: 2011/3/31 15:24:36</p><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp; 根据学校、学院的统一安排和调整，将于今年把分散在化工楼、子良楼的化学实验教学中心的实验室全部搬迁到朝晖校区的存中楼三、四、五楼，实验室搬迁建设面积1365m2，搬迁后实验中心总面积比现有增加800 m2左右，从而形成化学实验教学中心朝晖校区存中楼、屏峰校区化学实验室两大教学基地。<br>&nbsp;&nbsp;&nbsp; 目前，由学校投资的实验室前期水电、通风管道建设已接近尾声，接下来将进行实验工作台、实验室内部通风设施、实验教学准备设施以及实验中心多媒体网络教学设施等的建设。化学实验教学中心于2010年底在学校的大力支持下，申报成功“浙江省教育厅实验室建设资金专项”，立项投入150万元，主要用于实验室内部建设以及新增实验仪器设备的购置。2011年3月30日，该建设方案通过了由浙江大学及本校教师组成的专家组论证，同意项目启动建设。专家论证意见认为：化学实验教学中心是浙江省第一批实验教学示范中心，每年承担了全校15个全日制专业（110多个班级，约3400名学生）的基础化学实验教学任务，年实验教学人时数大于19万。另外，还开设“综合化学实验”选修课，并为大学生课外科技活动和大学生科技创新提供实验条件。中心所承担的“基础化学实验”课程是国家精品课程，并在全国大学生化学实验邀请赛等活动中取得了好成绩。实验中心现有的实验室分散，实验用房拥挤，实验环境较差，不利于实验中心的教学和管理，限制了实验中心的进一步发展。建议学校进一步加大对中心的投入，更好地发挥省级示范中心的作用。</p><p style="margin-left:0px;">&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/IMG_3097.jpg" alt=""></figure><p>&nbsp;</p>', NULL, N'', N'2011/3/31 15:24:36', N'1', N'2020-11-20', N'admin', N'42', N'', N'', N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'B7478355-7BBF-4C70-8CDB-6CC829658CF0', N'0', N'47', N'', N'化学实验教学中心党支部举办基础化学实验技能辅导', N'<h2 style="margin-left:0px;text-align:center;"><strong>化学实验教学中心党支部举办基础化学实验技能辅导</strong></h2><p style="text-align:center;">发表于: 2011/3/29 8:20:50</p><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp; 3月25日-26日，化学实验教学中心党支部为化材学院2007级学生举办基础化学实验技能辅导，学生报名64人。<br>&nbsp;&nbsp;&nbsp; 基础化学实验操作是化学、化工及相关专业从事实验研究工作的最基本技能，要得到准确的实验结果必须要有正确规范的实验操作。浙江大学、华东理工大学等高校在相关专业的研究生入学复试中，都要加试化学实验操作。每年我校报考这些学校的学生，在收到复试通知后，都会找到化学实验教学中心的老师，要求帮他们复习实验基本操作。针对这种情况，化学实验教学中心党支部组织老师老师认真准备、备课，义务为考生们讲解，并提供实验场地让他们练习。由于化学实验教学中心日常的教学任务很繁重，实验场地很紧张，实验中心的党员老师们就利用双休日为学生们加班加点进行辅导，一方面复习巩固了基础化学实验基本知识和基本技能，另一方面也为学生们进一步深造打下了良好的基础。近五年来，参加辅导的学生已达到100多人次，这一活动已成为化学实验教学中心党支部每年一次的具有特色的“党日”活动。</p><p style="margin-left:0px;">&nbsp;</p><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp;&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/IMG_3079.JPG" alt=""></figure><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/IMG_3076.JPG" alt=""></figure><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/IMG_3088.JPG" alt=""></figure><p>&nbsp;</p>', NULL, N'', N'2011/3/29 8:20:50', N'1', N'2020-11-20', N'admin', N'43', N'', N'', N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'3B3D5332-CB51-4E0D-BC0A-BF3B193DB48A', N'0', N'48', N'', N'化学实验教学中心举行年度总结会', N'<h2 style="margin-left:0px;text-align:center;"><strong>化学实验教学中心举行年度总结会</strong></h2><p>发表于: 2011/2/22 15:09:22</p><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp; 2010年12月29日，省化学实验教学示范中心举行2010年度工作总结会，实验中心常务副主任强根荣总结了一年来中心的主要工作，分析了实验中心存在的问题，提出了明年的工作重点。<br>&nbsp;&nbsp;&nbsp; 过去的一年，在学院的领导和关心、支持下，通过中心广大教职工的共同努力，取得了显著的成绩。<br>&nbsp;&nbsp;&nbsp; 一、教书育人创佳绩<br>&nbsp;&nbsp;&nbsp; 1.过去的一年里，实验中心完成全日制实验教学人时数191804。在如此巨大的工作量面前，中心各实验室安全、平稳、有序地运行，为基础化学实验国家精品课程顺利建设营造了良好的氛围。<br>&nbsp;&nbsp;&nbsp; 2.组织指导了全国、全省化学实验竞赛：（1）第七届全国大学生化学实验邀请赛，获二等奖1人，获三等奖2人；（2）第四届全省高校基础化学实验技能大奖赛，分获实验操作一等奖及三等奖各1人，实验理论二等奖1人。<br>&nbsp;&nbsp;&nbsp; 3.指导大学生课外科技活动，新增学校创新实验计划项目1项、浙江省大学生科技创新项目（新苗人才计划）1项，以大二学生为第一作者在核心刊物上发表论文1篇，获学校“运河杯”课外科技活动三等奖1项。<br>&nbsp;&nbsp;&nbsp; 4.获学校“三育人”先进个人荣誉1人。</p><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp; 二、课程建设创新高<br>&nbsp;&nbsp;&nbsp; 1.凝聚学院、化学学科的共同力量，“基础化学实验”课程被教育部批准立项为国家精品课程建设，课程建设水平一年上一个台阶。<br>&nbsp;&nbsp;&nbsp; 2.进一步完善化学实验教学体系，继续开设“综合化学实验”选修课，取得显著成效：<br>&nbsp;&nbsp;&nbsp; （1）“综合化学实验课程的建设和发展”项目被列为学校教改项目；<br>&nbsp;&nbsp;&nbsp; （2）《综合化学实验》教材被列为学校重点建设教材；<br>&nbsp;&nbsp;&nbsp; （3）《综合化学实验》由化学工业出版社正式出版；<br>&nbsp;&nbsp;&nbsp; （4）“融基本技能提高和创新能力培养于一体的综合化学实验教学与研究”申报学校教学成果培育项目，期望能获得更好的成果。</p><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp; 三、学习交流创示范<br>&nbsp;&nbsp;&nbsp; 1.在第七届浙江省高校化学实验教学中心主任联席会议上，计伟荣老师代表中心作了“融创新教育于基础教学”的报告，全面介绍了我校基础化学实验精品课程的建设情况。<br>&nbsp;&nbsp;&nbsp; 2.参加了全国第五届“大学化学化工课程报告论坛”，对提升教学理念、改革教学方法、创新教学模式，提供了很好的学习和借鉴机会。<br>&nbsp;&nbsp;&nbsp; 3.先后接待了太原理工大学、衢州学院等院校化学实验教学中心的来访。太原理工大学就实验课程的设置、实验时数的分配、实验教材的建设及实验中心的管理等问题，进行了广泛的交流。衢州学院在中心的一个星期里，他们详细听取了我校基础化学实验的讲课，查看了学生实验操作，了解了实验示范中心的建设情况。同行们对我校基础化学实验课程和实验中心的建设给予了高度评价，进一步扩大了示范、辐射作用。</p><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp; 四、团结一致创未来<br>&nbsp;&nbsp;&nbsp; 在新的一年里，除了做好日常的教学工作，实验中心面临着繁重的建设任务，需要全体教职工齐心协力，团结一致，共同把工作做细、做踏实。<br>&nbsp;&nbsp;&nbsp; 1.实验室建设：<br>&nbsp;&nbsp;&nbsp; （1）实验室搬迁：主要是有机化学实验室、物理化学实验室。申报了教育厅的实验室建设专项“化学实验教学中心续建（150万元）”、学校的实验室建设项目“基础化学实验导训实训系统建设（20万元）”，目前有关建设经费基本落实，建设方案正在论证启动，对提升实验中心形象和实验教学质量起到了积极作用。<br>&nbsp;&nbsp;&nbsp; （2）实验室搬迁后，实验室面积扩大，相应的实验台（套）数增加，要落实经费，做好常规仪器设备的建设，否则，将影响正常的实验教学。<br>&nbsp;&nbsp;&nbsp; 2.基础化学实验选课情况：在本学期“有机化学实验”选课试点运行后，下学期“无机及分析化学实验”、“物理化学实验”、“有机化学实验”课程全部进入学校原创教务管理系统，由学生自主选择实验时间，从而为增强学生自主学习能力起到了很好的推动作用，有关选课过程中存在的问题需要教务处、学院、中心共同解决。<br>&nbsp;&nbsp;&nbsp; 3.课程建设情况：<br>&nbsp;&nbsp;&nbsp; （1）补充完善“基础化学实验理论在线自测系统”建设，目前已列为学院教改项目，需要进一步提高质量和水平，争取明年申报学校教改项目；<br>&nbsp;&nbsp;&nbsp; （2）实验室搬迁后，要抓紧落实主要、典型实验的录像拍摄及上网，完成《基础化学实验国家精品课程申报书》的承诺；<br>&nbsp;&nbsp;&nbsp; （3）完善基础化学实验教案修订上网工作，目前“有机化学实验”教案已全部上网，“物理化学实验”、“无机及分析化学实验”教案争取下学期全部上网；<br>&nbsp;&nbsp;&nbsp; 4.实验室日常管理工作：要特别注意实验室的安全和卫生工作，做到“工作再忙，安全不忘”，加强实验室工作人员的自我约束和管理，为国家精品课程的建设添砖加瓦。<br>&nbsp;&nbsp;&nbsp; 本着相互学习和交流的目的，中心全体教职工对一年来自己的工作进行了述职。<br>&nbsp;&nbsp;&nbsp; 最后，实验室与资产管理处处长、实验示范中心主任计伟荣作了总结讲话，对实验中心一年来的工作和所取得的成绩给予充分肯定，对中心全体教职工所付出的努力表示衷心感谢。他强调了做好基础化学实验课程建设的重要性，进一步明确了明年课程建设的重点，同时对实验中心的发展提出了更高的要求。</p><p style="margin-left:0px;">&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/IMG_2992.jpg" alt=""></figure><p>&nbsp;</p>', NULL, N'', N'2011/2/22 15:09:22', N'1', N'2020-11-20', N'admin', N'44', N'', N'', N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'D0554B40-8BEC-4BF6-9E1A-42CBA0FD05A2', N'0', N'49', N'', N'太原理工大学基础化学实验中心来访', N'<p style="text-align:center;">发表于: 2011/2/22 15:03:52</p><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp; 2010年12月14日，太原理工大学基础化学实验中心主任谢鲜梅教授等两人，来我实验中心交流访问。<br>&nbsp;&nbsp;&nbsp; 中心常务副主任强根荣向客人们介绍我校基础化学实验教学情况。双方就基础化学实验的课程设置、教学时数、教学内容改革以及精品课程建设等问题，进行了深入的探讨，并交流了进一步做好综合化学实验教学建设的一些想法。</p><p>&nbsp;</p>', NULL, N'', N'2011/2/22 15:03:52', N'1', N'2020-11-20', N'admin', N'45', N'', N'', N'0')
