INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'A5CAAFD2-6657-465A-8296-7C9C02AF8BDC', N'0', N'50', N'', N'衢州学院来化学实验教学中心参观交流', N'<p>发表于: 2011/2/22 15:01:28</p><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp;&nbsp; 2010年10月18日至10月23日，衢州学院基础化学实验室主任潘向军老师等一行五人，来化学实验教学中心参观、交流。他们实地听取了我校老师对“有机化学实验室安全及规则教育”、“蒸馏与分馏”、“气体摩尔常数测定”、“酸碱标准溶液的配制与标定”、“溶液表面张力的测定”、“电动势的测定”等实验的讲解，并观看了学生的操作。他们还就省级化学实验教学示范中心的建设等有关问题，进行了深入细致地了解和探讨。<br>&nbsp;&nbsp;&nbsp; 客人们对我化学实验教学中心的评价是：1.有先进的教学理念，课堂信息量大，组织有序，教学效果好；2.注重实验操作技能培养；3.制度完善，管理到位。</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/IMG_2839.jpg" alt=""></figure><p>&nbsp;</p>', NULL, N'', N'2011/2/22 15:01:28', N'1', N'2020-11-20', N'admin', N'46', N'', N'', N'0')
