INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'0CF47A81-43F6-4E34-8320-AC17A4981C32', N'0', N'51', N'', N'我校在第四届浙江省高校基础化学实验技能竞赛中获奖', N'<h2 style="margin-left:0px;text-align:center;"><strong>我校在第四届浙江省高校基础化学实验技能竞赛中获奖</strong></h2><p>发表于: 2011/2/22 14:57:11</p><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp; 2010年10月5日-7日，第四届浙江省高校基础化学实验技能大赛在杭州师范大学举行。我校化材学院苏新立获得实验操作技能竞赛一等奖，化材学院牛欢获得实验理论竞赛二等奖，健行学院吴越获得实验操作技能竞赛三等奖。同时，我校还获得优秀组织奖。<br>&nbsp;&nbsp;&nbsp; 基础化学实验技能竞赛分为实验理论竞赛和实验操作竞赛两部分，内容涵盖化学化工专业本科的基础化学实验内容，主要包括无机、有机、分析和物化等四门课程的实验教学内容。本次赛事由浙江省化学会和浙江省化学化工教学指导委员会主办，杭州师范大学承办。比赛旨在推动我省高校化学化工专业的实践教学改革，扩大化学实验教学成果的影响，突出基础化学实验在本科教学过程中的重要地位；探索提高大学生化学实验能力的思路、途径和方法，培养学生严谨的学习态度、科学的研究方法和认真细致的思维方式，提高学生动手操作实验的能力和综合素质。<br>&nbsp;&nbsp;&nbsp; 为选拔本次大赛的参赛选手，我校于今年5月开始组织了校赛，全校共有69名学生报名参加，涵盖化材、药学、健行等多个学院。经初赛选拔、专业培训和决赛比拼，最终苏新立、谢涛、牛欢、徐佳琪、吴越等5位同学获得省赛资格。</p><p style="margin-left:0px;">&nbsp;</p><p style="margin-left:0px;">&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/IMG_2838.jpg" alt=""></figure><p style="margin-left:0px;">&nbsp;&nbsp; &nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/%E5%9B%BE%E7%89%871.jpg" alt=""></figure><p>&nbsp;</p>', NULL, N'', N'2011/2/22 14:57:11', N'1', N'2020-11-20', N'admin', N'47', N'', N'', N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'BDA959B8-640D-4F72-86B6-ECFA586D0E9A', N'0', N'52', N'', N'我校学生在第七届全国大学生化学实验邀请赛中取得佳绩', N'<h2 style="margin-left:0px;text-align:center;"><strong>我校学生在第七届全国大学生化学实验邀请赛中取得佳绩</strong></h2><p>发表于: 2011/2/22 14:49:24</p><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp; 2010年7月11日-15日，第七届全国大学生化学实验邀请赛在武汉大学顺利举行。我校化材学院刘哲益获二等奖，化材学院谢涛、黄杏芬分别获得三等奖。<br>&nbsp;&nbsp;&nbsp; 全国大学生化学实验邀请赛是在教育部和国家自然科学基金委的大力支持下，由教育部高等学校化学教育研究中心主办的我国高等学校化学学科最高级别赛事。邀请赛旨在推动我国高等学校化学实验教学模式、教学内容、教学方法等的改革，探索培养创新型化学人才的思路、途径和方法，以提高我国化学实验教学总体水平。<br>&nbsp;&nbsp;&nbsp; 自1998年以来，邀请赛已先后在南开大学、吉林大学、北京大学、厦门大学、中山大学和浙江大学成功地举办了六届，对提高我国本科生的实践能力和科学素质、推进化学实验教学改革发挥了重要作用，产生了深远的影响。<br>&nbsp;&nbsp;&nbsp; 按照大赛章程，参赛学校按本校化学(或应化或化工)专业大三学生总人数的30% 提供学生名单，大赛组委会从候选学生名单中随机抽出三名学生作为该校正式参赛选手，在比赛开始前，确定各位学生比赛的科目（无机及分析化学实验、有机化学实验、物理化学实验中的一门），从而有效地促使学生全面掌握化学实验知识与操作技能，客观地反映了学生的实际水平，保证了大赛的公正性、公平性。通过比赛，真正考察了学生的实验观察能力、分析能力、解决问题能力，树立了实验创新意识；促进了全国各大高校的交流，营造了良好的实验钻研氛围，巩固了学生们实验操作的基础，倡导了踏实独立的实验品格，培养了严谨求实的科研精神。<br>&nbsp;&nbsp;&nbsp; 本届全国大学生化学实验邀请赛在武汉大学举行，有北京大学、清华大学、南京大学、浙江大学、复旦大学、厦门大学、南开大学等41所知名高校参赛，并有国防科技大学等3所学校观摩。竞赛内容主要包括实验理论笔试和实验操作竞赛。经过激烈角逐，大赛共评选出一等奖14名，二等奖26名，三等奖83名以及承办学校的特别奖3名。<br>&nbsp;&nbsp;&nbsp; 邀请赛期间同时举办了“全国化学实验教学与实验室建设研讨会”，与会代表围绕化学实验教学改革、理论与实验结合、实验室建设与管理三个主题展开了讨论与交流。</p><p style="margin-left:0px;">&nbsp;</p><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/IMG_2599.jpg" alt=""></figure><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/IMG_2556.jpg" alt=""></figure><p>&nbsp;</p>', NULL, N'', N'2011/2/22 14:49:24', N'1', N'2020-11-20', N'admin', N'48', N'', N'', N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'45DEDCA9-7CF2-4654-A519-553B4CDBA731', N'0', N'53', N'', N'我校《综合化学实验》教材出版', N'<h2 style="margin-left:0px;text-align:center;"><strong>我校《综合化学实验》教材出版</strong></h2><p style="text-align:center;">发表于: 2011/2/22 14:39:42</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/%E7%BB%BC%E5%90%88%E5%8C%96%E5%AD%A6%E5%AE%9E%E9%AA%8C%E6%95%99%E6%9D%90.jpg" alt=""></figure><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp; 2010年7月，我校《综合化学实验》教材由化学工业出版社正式出版。<br>&nbsp;&nbsp;&nbsp; 随着化学实验教学改革的不断深入，根据创新实验教学体系的要求，各校在基础实验教学的基础上，纷纷建立了综合化学实验课程，编写了相应的实验教材。在改革的过程中，我们提出了“重视基本技能、突出创新教育、依托学科优势、打造精品课程”的总体思路，在总结多年来教学改革实践经验的基础上，将实验基本操作、基本技能与创新意识、综合分析能力、研究设计能力的培养贯串于整个实验教学过程中，并且将大学生课外科技活动和大学生创新实验计划项目等融入实验教学，由此建立了“三个层次（基础规范性实验—综合设计性实验―研究探索性实验）、一个创新”的化学实验教学新体系，编写了与之相适应的基础化学实验系列教材。本书是在前三届试用的《综合化学实验》讲义的基础上经内容增删、调整改编而成的。<br>&nbsp;&nbsp;&nbsp; 本书选题内容主要基于以下三个方面：<br>&nbsp;&nbsp;&nbsp; 1.坚持“固基强本”的实验传统，巩固基础，加强技能，对原有实验讲义进行整理、删减、充实、提高，同时也汲取了少量国内外同类教材的新颖内容。<br>&nbsp;&nbsp;&nbsp; 2.采用“取之于学生，用之于学生”的办法，将近几年大学生创新实验计划项目及大学生科技创新项目的内容引用到教学实验中。<br>&nbsp;&nbsp;&nbsp; 3.按照“教学与科研互动”的原则，促使“科研成果教学化，教学内容研究化”，将教师的科研成果中适合于教学的内容引入到教学中，充实实验内容，反映各学科领域中的新成果、新技术、新方法以及现代实验技术与手段，体现综合化学实验内容的新颖性、先进性。<br>&nbsp;&nbsp;&nbsp; 本教材的特色及创新之处在于：<br>&nbsp;&nbsp;&nbsp; 1. 贯穿“基础规范性实验—综合设计性实验—研究探索性实验”的主线，在已经完成的基础化学实验教材的基础上，将实验的综合设计性和研究探索性凸显在本教材中。<br>&nbsp;&nbsp;&nbsp; 2. 彰显化学实验“绿色化”，将“绿色化学”的理念和内容渗透到实验教学过程中，充实绿色化学实验教学内容和实验手段。<br>&nbsp;&nbsp;&nbsp; 3. 着眼工科学生化学实验理论和操作的教育，让学生能综合应用化学知识、多种化学研究方法和技术，在教学过程中融入创新意识、激发创新思维、锻炼创新能力。<br>&nbsp;&nbsp;&nbsp; 本书编写过程中，得到了浙江工业大学重点建设教材项目的资助。</p><p>&nbsp;</p>', NULL, N'', N'2011/2/22 14:39:42', N'1', N'2020-11-20', N'admin', N'49', N'', N'', N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'750A845F-720E-4FF9-96CD-BF1C85957263', N'0', N'54', N'', N'我校基础化学实验课程被批准为2010年度国家精品课程建设', N'<h2 style="margin-left:0px;text-align:center;"><strong>我校基础化学实验课程被批准为2010年度国家精品课程建设</strong></h2><p>发表于: 2011/2/22 14:28:26</p><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp; &nbsp;2010年6月2日，我校基础化学实验课程通过教育部评审，被批准为2010年度国家精品课程建设。<br>&nbsp;&nbsp;&nbsp; 近五年来，基础化学实验课程的建设与发展，紧紧围绕浙江工业大学“以浙江精神办学、与浙江经济互动”的办学理念和人才培养定位，以创新型国家建设对人才培养的要求为导向，以“重视基本技能、突出创新教育”为基本思路，以实践能力和创新品质培养为核心和最终目标，在优化重构实验教学体系、集成打造实验教学平台、创新实验教学模式、更新实验教学内容、完善教学方法和手段、强化队伍建设、健全管理体制机制等方面进行了较为系统的研究与探索， 建立了“三层次、一创新”的实验教学新体系和创新培养贯穿于教学全过程的新模式。形成了一支结构合理、经验丰富、敬业爱岗的高水平实验教学师资队伍。教材建设、教学研究与改革成果丰硕，实验教学特色鲜明，平台先进，教学方法和教学手段改革成效明显，教学效果优良。本课程在浙江省处于领先水平，在国内高校同类课程中处于先进水平。<br>&nbsp;&nbsp;&nbsp; 课程的主要特色是：（1）创建了“三层次、一创新、递进式”化学实验教学新体系，强化了基本技能训练，突出了创新意识、创新思维到创新能力的层次化培养。（2）将绿色化学理念、技术和内容融入实验教学过程中，开发实施了三废处理、微波合成、微反应等实验，树立了节能环保意识，提高了知识综合应用能力。（3）首创全省高校大学生化学实验竞赛，并将课外科技活动和创新实验计划项目融入实验教学，建立了创新实验教学新平台，起到了引领和示范作用。</p><p style="margin-left:0px;">附件：<a href="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/%E6%95%99%E8%82%B2%E9%83%A8%20%E3%80%80%E8%B4%A2%E6%94%BF%E9%83%A8%E5%85%B3%E4%BA%8E%E6%89%B9%E5%87%862010%E5%B9%B4%E5%BA%A6%E5%9B%BD%E5%AE%B6%E7%B2%BE%E5%93%81%E8%AF%BE%E7%A8%8B%E5%BB%BA%E8%AE%BE%E9%A1%B9%E7%9B%AE%E7%9A%84%E9%80%9A%E7%9F%A5.doc">1.&nbsp; 教育部、财政部关于批准2010年度国家精品课程建设项目的通知</a></p><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;2.&nbsp; <a href="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/2010%E5%B9%B4%E5%BA%A6%E5%9B%BD%E5%AE%B6%E7%B2%BE%E5%93%81%E8%AF%BE%E7%A8%8B%E5%90%8D%E5%8D%95.xls">2010年度国家精品课程名单</a></p><p>&nbsp;</p>', NULL, N'', N'2011/2/22 14:28:26', N'1', N'2020-11-20', N'admin', N'50', N'', N'', N'0')
