INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'94D12A9E-7193-4C34-B254-1CD49E66A56E', N'0', N'55', N'', N'艾宁副院长来化学实验教学中心检查工作', N'<h2 style="margin-left:0px;text-align:center;"><strong>艾宁副院长来化学实验教学中心检查工作</strong></h2><p>发表于: 2011/2/22 14:08:32</p><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp;&nbsp; 2010年3月12日，在基础化学实验课程申报国家精品课程的紧张过程中，艾宁副院长来化学实验教学中心检查、指导工作。<br>&nbsp;&nbsp;&nbsp; 艾宁副院长仔细听取了课程的申报情况，了解了申报过程中还存在的具体困难，表示学院将全力以赴支持申报工作，有些困难将会同学院各方面力量给予克服。<br>&nbsp;&nbsp;&nbsp; 艾宁副院长还检察了基础化学实验的日常教学情况和实验室安全问题。</p><p style="margin-left:0px;">&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/IMG_1792.jpg" alt=""></figure><p>&nbsp;</p>', NULL, N'', N'2011/2/22 14:08:32', N'1', N'2020-11-20', N'admin', N'51', N'', N'', N'0')
