INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'91B2D4AF-26D2-4B9D-B893-D74D227236C6', N'0', N'56', N'', N'高建荣院长检查指导化学实验教学中心工作', N'<h2 style="margin-left:0px;text-align:center;"><strong>高建荣院长检查指导化学实验教学中心工作</strong></h2><p>发表于: 2011/2/22 14:08:06</p><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp; 2010年3月2日，高建荣院长来化学实验教学中心检查工作。<br>&nbsp;&nbsp;&nbsp; 高院长详细听取了实验中心近期的工作汇报，深入到物理化学实验室听取了实验指导教师的讲课，观看了学生的实验操作，对学生实验中存在的问题提出了要求，并仔细地检查了学生的实验记录本。<br>&nbsp;&nbsp;&nbsp; 高院长还详细询问了基础化学实验课程申报国家精品课程的情况，要求大家再接再厉，严格按照教育部要求做好各方面工作，争取我院的课程建设上一个新台阶。他还表示学院将从各方面全力保障申报工作的顺利进行。</p><p style="margin-left:0px;">&nbsp;</p><figure class="image"><img src="http://hxsyzx.dodokon.com/zgdhx/Upload/FCKfile/IMG_1806.jpg" alt=""></figure><p>&nbsp;</p>', NULL, N'', N'2011/2/22 14:08:06', N'1', N'2020-11-20', N'admin', N'52', N'', N'', N'0')
