INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'CDE4F580-88A3-4086-8FC7-6D60E72CDF1E', N'0', N'76', NULL, N'校企联合虚拟仿真实验室', N'<p align="left">
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 化学化工虚拟仿真实验室与北京东方仿真、上海曼恒数字联合共建浙江工业大学-东方仿真、浙江工业大学-曼恒数字联合虚拟仿真实验室。校企联合虚拟仿真实验室的主要职能包括：追踪虚拟仿真技术在化学化工等专业实验教学领域的最新发展方向；根据化学化工实验实训教学需要，提出虚拟仿真实验开发项目；校内中心教师提供化工实验素材（包括工艺流程和操作参数、化工装置结构及规格参数、化工实验原理和实验步骤等相关专业信息和资料），合作企业提供研究开发的技术支持，校企双反合作完成开发任务，学校网络中心提供运行条件和指导。研发的实验实训教学资源本教学中心试运行正常后，由联合实验室校企双方合作，共同向院校或企业推广，共同受益。</p>
<p align="left">
	<strong>代表企业</strong><strong>1</strong><strong>：北京东方仿真软件技术有限公司</strong></p>
<p align="left">
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 北京东方仿真软件技术有限公司是东方仿真集团下属子公司，成立于1999年，是北京市属高新技术企业和软件企业，专门从事计算机仿真应用领域高科技产品研究开发，形成了基于&ldquo;流程工业仿真平台（PISP）&rdquo;和&ldquo;企业E-learning网络培训平台TRMS&rdquo;两大核心平台基础的多系列自有知识产权仿真培训软件系统，涵盖油气工程、石油加工、化工、水处理、生物制药、食品等多个流程工业领域。东方仿真也是中国大陆目前规模最大，年实施项目数量最多的流程工业OTS服务商，占中国石化领域仿真系统项目的70%以上，多项技术及产品项目获得了省部级科技进步奖。东方仿真以仿真为手段，将数字仿真技术，多媒体技术，网络技术相整合，逐渐向拥有&ldquo;先进技术的职业技能培训方案咨询商、服务商&rdquo;转型，为1000多个院系（包括本科及职业学校）提供了上万套各类仿真软件，满足了学校辅助教学、辅助实验和辅助实习实训的需要。东方仿真的ESST团队专业领域广泛，可以提供满足大型新建装置开车、安全培训或其他目的OTS项目的整体方案咨询及实施；大型工业企业内部，建立包括在线OTS和E-learning平台的整体方案咨询及实施；提供通用类仿真软件产品；提供仿真开发平台PISP和E-Learning平台满足用户二次开发；企业或学校培训（实训）中心的建设。</p>
<p align="left">
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 东方仿真在浙江工业大学建立校企联合虚拟仿真实验室，除委派3名研发工程师外，还以强大的技术团队为后盾。在完成校企联合虚拟仿真实验室相关职称的基础上，重点关注虚拟仿真实验支撑平台和化工过程流程模拟仿真等两大方面。</p>
<p align="left">
	<strong>代表企业</strong><strong>2</strong><strong>：上海曼恒数字技术有限公司</strong></p>
<p align="left">
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 曼恒数字（Graphic Design Information Co.,Ltd ）创建于2007年，是国内领先的<a href="http://www.gdi.com.cn/">虚拟仿真</a>技术研发机构和供应商、上海市高新技术企业和市科委重点支持的挂牌软件企业，先后获得科技部创新基金、上海市科学与艺术创新奖、最具活力科技企业等多项荣誉称号。曼恒数字专注于研发三维图形设计、虚拟和仿真技术的产品与服务，以&ldquo;三维技术，创造虚拟世界&rdquo;为发展理念，不断推动<a href="http://www.gdi.com.cn/">三维虚拟技术</a>的创新发展，打造了IT实践、研发经验丰富的专业团队，建立了规模超过百人的三维技术研发中心&mdash;&mdash;曼恒三维图形技术研究院。曼恒数字在行业内率先提出了应用程序与3D技术相结合的全新概念&mdash;AB3D（Application Base on 3D），基于该理念于2010推出了自主研发的三维<a href="http://baike.baidu.com/view/1033797.htm">虚拟仿真</a>平台DVS3D系统，开拓了基于互联网的跨平台3D内容服务平台，该平台将通过Web3d技术打造新的互联网媒介，为教育、科研、游戏、影视、广告、工程设计等行业提供三维数字化内容服务，加快普及3D技术走进家庭、娱乐和商业等场所，引领全新的互联网媒介时代创新发展。</p>
<p align="left">
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 曼恒数字在浙江工业大学建立校企联合虚拟仿真实验室，除委派3名研发工程师外，还以强大的技术团队为后盾。在完成校企联合虚拟仿真实验室相关职称的基础上，重点关注3D虚拟仿真实验室建设。</p>
<p align="left">
	<img alt="" src="/UploadFiles/fck/images/xiaoqigongjian.JPG" style="width: 730px; height: 168px" /></p>', NULL, NULL, N'2013-04-21 16:57:37.000', N'1', N'2020-11-20', N'admin', N'72', NULL, NULL, N'0')
