INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'EC570658-8313-41B0-9B31-CA4EFFAC58CF', N'0', N'136', NULL, N'南京大学张剑荣教授来我校作实验教学专题报告', N'<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 10月24日下午，南京大学国家级化学实验教学示范中心主任张剑荣教授应我校化学化工实验教学中心的邀请，在浙江工业大学第28期教学论坛作实验教学专题报告。张教授以&ldquo;对实验教学改革中若干问题的认识&rdquo;为题，从&ldquo;理念和思路&rdquo;、&ldquo;体系和内容&rdquo;、&ldquo;方法和手段&rdquo;、&ldquo;安全与管理&rdquo;四个方面，提出了现代人才培养对实验教学改革的新要求。特别是在实验教学课程体系和内容建设方面，张教授以&ldquo;一体化、多层次、开放式&rdquo;为指导思想，改革课程内容，以南京大学具体的教学内容为例，讲述了&ldquo;基础性、综合性、研究性&rdquo;实验的实施情况。在综合性、研究性实验方面，既有化学一级学科范畴内的综合化学实验内容，又有跳出一级学科范畴的内容，如包含化学与生物、化学与环境等交叉内容的实验，另外，开设许多贴近生活、贴近实际的教学内容，以此&ldquo;激发学生兴趣、训练知识应用、引导知识创造、培养团队协作&rdquo;。在教学手段和方法现代化方面，张教授报告了&ldquo;PBL（问题式教学法）&rdquo;、&ldquo;MOOC（慕课）&rdquo;、&ldquo;FCM（翻转课堂教学模式）&rdquo;等教学方法在实验教学中的应用。同时，张教授以国内外大量的实验室安全事故为例，指出了实验室建设的新要求、新措施。<br />
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 报告结束后，与会者就我校实验教学中的相关问题与张教授进行了交流。<br />
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 张剑荣，1963年生，南京大学教授，博士生导师。1980年-1990年，先后在南京大学获得学士、硕士和博士学位。现任南京大学教师教学发展中心专家委员会委员、南京大学赵世良讲座教授、国家级化学实验教学示范中心主任。曾赴美国、德国、法国、日本、新加坡、台湾地区等考察大学基础教学和学术交流。2001年、2005年和2009年分别获得国家级教学成果二等奖。主要从事生物燃料电池、纳米光电材料及其生物传感应用研究；2010年获得教育部自然科学奖一等奖， 2007年和2008年分别获得江苏省科技进步奖二等奖，发表SCI论文100余篇。</p>
<p style="text-align: center">
	<img alt="" src="/UploadFiles/fck/images/IMG_69831.JPG" style="width: 474px; height: 333px" /><br />
	&nbsp;</p>', NULL, NULL, N'2014-10-26 16:10:23.000', N'1', N'2020-11-20', N'admin', N'132', NULL, NULL, N'0')
GO

