INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'B0A97DA7-ADDF-4CC0-A792-F507D6E68FC4', N'0', N'140', NULL, N'化学化工实验教学中心召开全体教师工作会议', N'<p align="left">
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 5月5日下午，化学化工实验教学中心全体教师工作会议在存中楼804召开。会议由学院副院长、化学化工实验教学中心常务副主任艾宁主持，化学化工实验教学中心全体教师参加了会议。</p>
<p align="left">
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 艾宁副院长回顾了中心近年来取得的辉煌成绩，特别是化学化工国家级虚拟仿真实验教学中心和化学化工国家级实验教学示范中心的申报成功，标志着实验中心进入了一个新的发展阶段。他对实验中心的队伍建设和教师个人发展、实验中心的示范辐射作用、实验中心目前存在的问题和今后的工作方向等作了指示。中心副主任强根荣、杨阿三老师针对各自分管的工作作了补充，并对今后的发展提了建议。各位教师就中心的建设和发展进行了热烈讨论。会议还布置了化学化工实验教学中心的近期工作。</p>
<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 浙江工业大学化学化工实验教学中心由省级化学实验教学示范中心和省级化工实验教学示范中心为基础组建而成，通过不断的建设发展，2013年建成国家级虚拟仿真实验教学中心，2014年获批国家级实验教学示范中心。目前，中心面向全校21个本科专业开设实验课程13门，其中，&ldquo;基础化学实验&rdquo;是国家级精品课程和国家级精品资源共享课程，&ldquo;化工综合实验&rdquo;是校级优秀课程，每年承担的实验教学人时数达到23万。</p>
<p style="text-align: center">
	<img alt="" src="/UploadFiles/fck/images/15-5-5会议.JPG" style="width: 412px; height: 183px" /></p>', NULL, NULL, N'2015-05-06 21:31:31.000', N'1', N'2020-11-20', N'admin', N'136', NULL, NULL, N'0')
GO

