INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'E6E787E3-848C-4B6C-AFCF-DD4AC5126553', N'0', N'144', NULL, N'实验中心教师指导学生参加大学生化工设计竞赛获佳绩', N'<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 近日，第九届大学生化工设计竞赛全国总决赛在天津大学落幕。由周玉立、张哲明、张武杰、单若妮和魏梦怡五位同学组成的&ldquo;开拓者&rdquo;团队，代表我校参加全国总决赛并获得全国特等奖，同时以华东赛区总决赛第五名的成绩获得华东赛区特等奖。<br />
	　　本届竞赛题目是为某一大型综合化工企业设计一座采用清洁生产工艺制取乙二醇（EG）的分厂。要求学生根据实验、调研和查阅文献所得的数据，完成项目可行性论证、工艺流程设计、设备选型及典型设备设计、车间设备布置设计、工厂总体布置设计、经济分析与评价、设计说明书编制等自拟定的子系统化工厂设计。<br />
	　　该项竞赛由中国化工学会、中国化工教育协会、教育部高等学校化学工程与工艺专业教学指导分委员会主办。本届共有238所高校的920支队伍参赛。经过初赛评审，共有来自全国华东、华北、华南、西北、华中、东北和西南等7个赛区的48支队伍进入全国总决赛。<br />
	　　此外，经过前期省赛的网评、会评和答辩，我校由郑琳、丁伟昌、方志颖、周星和叶海娜组成的&ldquo;UP TEAM&rdquo;团队获得省一等奖，由黄杰、孙嘉、郑值、闵灿灿和毛小宁组成的&ldquo;POWER TEAM&rdquo;等另外7支团队获得省三等奖。</p>', NULL, NULL, N'2015-09-11 08:38:38.000', N'1', N'2020-11-20', N'admin', N'140', NULL, NULL, N'0')
GO

