INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'6ECB8CF2-2EF6-4BD1-A2F1-2152304DDE1A', N'0', N'148', NULL, N'转发：我校完成“浙江省首批数字校园示范校”建设项目验收', N'<h3 style="margin: 0px; padding: 5px 0px 0px 11px; list-style-type: none; height: 27px; color: rgb(43, 157, 253); font-family: Simsun; background-image: url(&quot;../img/icon-2.jpg&quot;); background-repeat: repeat-x;">
	&nbsp;</h3>
<h3 style="margin: 0px; padding: 5px 0px 0px 11px; list-style-type: none; height: 27px; color: rgb(43, 157, 253); font-family: Simsun; background-image: url(&quot;../img/icon-2.jpg&quot;); background-repeat: repeat-x;">
	<span style="color: rgb(67, 67, 67); line-height: 27px;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;5月26日，浙江省教育信息化工作领导小组办公室组织专家对我校&ldquo;省首批数字校园示范校&rdquo;建设项目进行了现场验收。专家组由浙江大学、杭州电子科技大学和浙江旅游职业技术学院的教授组成。副校长陈建孟及相关部门（项目）负责人参加了验收会。</span><br style="margin: 0px; color: rgb(67, 67, 67); line-height: 27px;" />
	<span style="color: rgb(67, 67, 67); line-height: 27px;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;验收会上，信息化办公室向专家组做了题为&ldquo;支持实验实践教学的创新信息化环境建设&rdquo;的专题汇报，详细报告了项目建设目标、任务完成情况、项目建设成果及不足与展望的情况。随后，化学工程学院、机械工程学院、学生处分别做了创新案例的汇报。专家组认真听取汇报、观看现场系统演示、审阅相关支撑材料，实地考察了机械工程学院创新实验室、国家级化学化工虚拟仿真实验教学中心、国家级化学化工实验教学示范中心、微格教室和学生综合事务大厅的信息化应用情况。</span><br style="margin: 0px; color: rgb(67, 67, 67); line-height: 27px;" />
	<span style="color: rgb(67, 67, 67); line-height: 27px;">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;验收反馈会上，专家组对我校数字校园示范校建设项目的目标任务完成情况和建设成效给予了充分肯定。专家组认为我校运用信息化的技术手段，结合学科与区域特点，建成虚实融合的化学化工和机械类仿真实验教学信息化平台及教学课程体系，培养学生创新能力，强化实践教学的改革，示范校建设成效明显，在同类院校中具有示范引领作用。专家组同时建议我校进一步完善信息化建设管理机制，总结提炼示范校建设的成功经验，形成教育教学创新案例及模式，不断将建设成果向同类院校推广。陈建孟代表学校作表态发言，对专家组的意见建议提出了下一步工作落实和要求，不断强化信息化顶层意识，全面推进信息技术与教育教学深度融合，提升学校信息化水平。</span></h3>', NULL, NULL, N'2016-06-16 13:11:53.000', N'1', N'2020-11-20', N'admin', N'144', NULL, NULL, N'0')
GO

