INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'7003AC1D-93E7-4498-BB49-7BA78477D4E6', N'0', N'157', NULL, N'2014年国家级实验教学示范中心联席会化学化工学科组会议在大连召开', N'<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2014年8月2日-4日，国家级实验教学示范中心联席会化学化工学科组会议在大连理工大学召开。本次会议的主题是：1.化学化工虚拟仿真实验中心建设；2.化学化工类实验室安全环保的管理与教育；3.化学化工类实验教学示范中心建设经验交流；4.研讨化学类专业标准中化学实验教学的要求。会议上，教育部高等教育司实验室处高东锋副处长、联席会主席、南京大学化学实验教学中心张剑荣教授、华东理工大学化学实验教学中心孙学芹教授、大连理工大学化学虚拟实验中心刘志广教授等专家、学者分别就上述主题作了专题报告。</p>
<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 我校化学化工国家级虚拟仿真实验教学中心强根荣、刘华彦老师参加了本次会议，并就我校化学化工虚拟仿真教学情况、下一步工作思路向与会者作了交流。会议决定在下半年适当时候将组织虚拟仿真教学的专题研讨会。</p>', NULL, NULL, N'2014-08-05 11:02:46.000', N'1', N'2020-11-20', N'admin', N'153', NULL, NULL, N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'1B0DABA2-7D7A-47BD-9199-B1FC763D390A', N'0', N'158', NULL, N'课程与实验室建设', N'<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2010年，&ldquo;基础化学实验&rdquo;课程被评为国家级精品课程。2013年，&ldquo;基础化学实验&rdquo;课程转型升级为国家精品资源共享课。以化学实验作为重要支撑的 &ldquo;物理化学&rdquo;课程是国家级双语教学示范课程，&ldquo;无机及分析化学&rdquo;和&ldquo;有机化学&rdquo;课程是省级精品课程。</p>
<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 化学实验教学中心是浙江省首批实验教学示范中心。化工实验教学中心于2013年通过省级实验教学示范中心验收。</p>
<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2013年化学化工虚拟仿真实验教学中心获批国家级虚拟仿真实验教学中心。</p>
<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 以&ldquo;基础化学实验&rdquo;和&ldquo;化工综合实验&rdquo;作为重要实践课程的化学工程与工艺、应用化学、生物工程、制药工程等4个专业先后被评为国家级特色专业，化学工程与工艺和应用化学是教育部综合改革试点专业，化学工程与工艺、应用化学、材料科学与工程、生物工程、制药工程、环境工程等6个专业被评为浙江省&ldquo;十二五&rdquo;优势专业；化学工程与工艺专业是教育部首批&ldquo;卓越工程师教育培养计划&rdquo;试点专业，也是浙江省省属高校中首个通过教育部全国工程教育专业认证的本科专业。化学工程与技术是浙江省重中之重一级学科，全国学科排名第14位。化学是浙江省重点一级学科。中心还为化学工程与技术一级学科博士点开设研究生实验教学课程，为培养高层次人才服务。</p>
