INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'DA136A30-2A39-439C-863E-D0E2332A6CB0', N'0', N'183', NULL, N'曼恒数字来化学化工虚拟仿真实验教学中心交流', N'<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2014年5月9日，上海曼恒数字技术有限公司华东区教育销售总监丁永军先生到访化工学院化学化工国家级虚拟仿真实验教学中心，就中心在获批国家级虚拟仿真实验教学中心后，双方开展虚拟仿真实验技术研发合作的可能性进行探讨交流。双方各自介绍了后期在化学化工虚拟仿真技术研发的设想，认为在前期多年合作的基础上，继续发挥各自的优势与特色，可以在知识学习类、实验操作类和工程工艺操作类虚拟仿真（包括半实物仿真工厂）等方面开展紧密合作，为高校、企业的教学、培训开发新一代化学化工专业虚拟仿真产品。</p>
<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 我校教务处、化工学院和实验教学中心教师参与本次交流活动。</p>', NULL, NULL, N'2014-05-12 08:21:24.000', N'1', N'2020-11-20', N'admin', N'179', NULL, NULL, N'0')
