INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'6F741D69-7062-44D7-8098-328B326F9AEC', N'0', N'184', NULL, N'转发：【10月24日教学论坛预告】实验教学中若干问题的认识与思考', N'<div id="wenzi">
	<div>
		一、主讲嘉宾：张剑荣</div>
	<div>
		&nbsp;</div>
	<div>
		二、时间：10月24日（本周五）下午13:30-15:00</div>
	<div>
		&nbsp;</div>
	<div>
		三、地点：邵科馆圆廊会议室</div>
	<div>
		&nbsp;</div>
	<div>
		四、网络报名：http://www.jxzx.zjut.edu.cn/forum</div>
	<div>
		&nbsp;</div>
	<div>
		五、讲座题目：对实验教学中若干问题的认识</div>
	<div>
		&nbsp;</div>
	<div>
		六、主讲人简介：</div>
	<div>
		&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 张剑荣，1963年生，南京大学教授，博士生导师。1980年-1990年，先后在南京大学获得学士、硕士和博士学位。现任南京大学教师教学发展中心专家委员会委员、南京大学赵世良讲座教授、国家级化学实验教学示范中心主任。曾赴美国、德国、法国、日本、新加坡、台湾地区等考察大学基础教学和学术交流。2001年、2005年和2009年分别获得国家级教学成果二等奖。主要从事生物燃料电池、纳米光电材料及其生物传感应用研究；2010年获得教育部自然科学奖一等奖， 2007年和2008年分别获得江苏省科技进步奖二等奖，发表SCI论文100余篇。</div>
</div>
<p>
	&nbsp;</p>', NULL, NULL, N'2014-10-23 11:25:16.000', N'1', N'2020-11-20', N'admin', N'180', NULL, NULL, N'0')
GO

