INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'96B408DB-A3AC-45AF-8F50-1C3AFB29A4F2', N'0', N'186', NULL, N'高校实验教学示范中心可持续发展暨教学质量提升研讨会在重庆召开', N'<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2018年4月13-16日，由高等学校国家级实验教学示范中心联席会、重庆大学及重庆市高校实验室工作研究会联合主办的&ldquo;高校实验教学示范中心可持续发展暨教学质量提升研讨会&rdquo;在重庆大学举办。参会学校包括天津大学、大连理工大学、浙江大学等来自全国各地272所高校558个实验教学中心和虚拟仿真中心的代表及高校相关职能部门负责人参会。</p>
<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 本次研讨会的议题主要包括高校实验教学示范中心的规范管理及内涵发展，创新创业人才培养方式及经验交流、虚拟仿真实验中心的建设、虚拟仿真项目开设及运行要点探讨，实验技术队伍建设，设备开放共享及实验教学质量评价体系的建设及应用等。</p>
<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 伴随着实践教学地位的不断凸显，传统的实践教学管理模式已经很难完全适应当前实践教学发展的需要。化学化工国家级实验教学示范中心及化学化工国家级虚拟仿真中心将加快推进实践教学管理的信息化进程，构建高效、互通的智能化实践教学综合管理平台，提高实践教学管理的质量和实践教学资源的共享率，提高实验技术骨干教师的学术水平和综合素质，全面提高实验教学质量。</p>
<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 我校化工学院李琰君老师参加了本次会议。</p>
<p>
	<img alt="" src="/UploadFiles/fck/images/1524033994319890(1).jpg" style="width: 700px; height: 394px;" /></p>', NULL, NULL, N'2018-04-25 16:15:12.000', N'1', N'2020-11-20', N'admin', N'182', NULL, NULL, N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'891B72A6-B945-4E65-911C-C013232F5B2A', N'0', N'187', NULL, N'我校2项国家虚拟仿真实验教学项目通过认定', N'<div id="jiacu" style="line-height:27px">
	<p>
		<font size="3">&ensp; &ensp; 近日，教育部高等教育司发布了《教育部关于公布首批国家虚拟仿真实验教学项目认定结果的通知》(教高函〔2018〕6号),我校化学工程学院艾宁教授负责的《合成氨生产综合仿真实习》和机械工程学院姚建华教授负责的《激光表面改性虚拟仿真实验》两个项目通过认定。</font></p>
	<p>
		<font size="3">&ensp; &ensp; 虚拟仿真实验教学项目是在适应信息化条件下知识获取方式和传授方式、教和学关系等发生革命性变化的背景下，推进现代信息技术融入实验教学项目、拓展实验教学内容广度和深度、延伸实验教学时间和空间、提升实验教学质量和水平的重要举措。教育部为贯彻落实习近平总书记关于强化实践育人工作的重要指示精神和全国高校思想政治工作会议精神, 深化信息技术与教育教学深度融合，提高高等教育实验教学质量和实践育人水平，决定于2017至2020年在普通本科高等学校开展示范性虚拟仿真实验教学项目建设工作。我校积极响应、组织发动相关学院认真准备，经过校级遴选、省级评审和教育部的专家评审与网上公示，我校的2项虚拟仿真实验教学项目取得佳绩。此次共有92所高校的105个虚拟仿真实验教学项目通过认定；中南大学获得认定的有3项，包括我校在内的11所高校有2个项目获得认定；其余80所高校均入围1项。</font></p>
	<p>
		<font size="3">&ensp; &ensp; 教育部将继续按照先建设应用、后评价认定、持续监测评估的方式，按建设规划分年度认定国家虚拟仿真实验教学项目，2018年将有22个专业大类、250个项目指标开展认定。</font></p>
</div>
<p>
	&nbsp;</p>', NULL, NULL, N'2018-06-26 12:36:54.000', N'1', N'2020-11-20', N'admin', N'183', NULL, NULL, N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'51B881C3-0861-43AB-A437-FDBD9577B479', N'0', N'188', NULL, N'中国人民警察大学基础课实验中心来访', N'<p>
	&nbsp; &nbsp; &nbsp; &nbsp; 2018年11月7日，中国人民警察大学（原武警学院）基础课实验中心副主任王永明教授一行，来化学实验教学中心参观交流。化学化工实验教学中心副主任强根荣接待了来访者，向客人们介绍了我校化学实验教学中心在实验室建设、课程建设、实验室管理等方面的情况，双方就实验室建设的理念、化学实验教学改革、实验考核、实验设备以及实验室开放、大学生课外科技活动等进行了深入、细致地交流。客人们表示此次来访学到了不少经验，收获很大，对我校实验中心自制的减压蒸馏小推车等颇感兴趣，期望用于他们的教学、科研实验中，以后将加强交流，促进共同提高。</p>
<p>
	&nbsp;<span style="font-size:10px;"><img alt="" src="/UploadFiles/fck/files/微信图片_20181107102044(1).jpg" style="width: 700px; height: 600px;" /></span></p>', NULL, NULL, N'2018-11-08 13:17:06.000', N'1', N'2020-11-20', N'admin', N'184', NULL, NULL, N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'C9B9A800-A83E-4E27-AB64-8DCD24D690A4', N'0', N'189', NULL, N'化学实验教学中心本学期初连续开展教研活动', N'<p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 3月7日，化学实验教学中心在屏峰校区举行教研活动。针对“基础化学实验I”新的教学大纲中的教学内容、教学重点难点等进行了交流，对教学过程中一些常见的问题以及如何有效地开展针对性的教学进行了分析，任课教师预做了本学期更新的实验内容，为更好地落实教学过程做好了准备。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 3月8日，化学实验教学中心在朝晖校区针对基础化学实验Ⅱ的有关教学问题，再次举行活动，围绕“乙酰二茂铁的制备及柱色谱分离”、“苯甲酸（微波）及苯甲酸乙酯的制备、减压蒸馏”等实验，对研究生助教和新聘任的指导教师进行了培训。</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 教学活动的顺利开展，为课程教学交流提供了良好的交流平台，有利于各任课教师教学经验的相互交流、学习，能更好的汇总各任课教师在教学过程中一些常见的教学问题，通过教学团队各老师的共同协作交流来有效地解决。</p><figure class="image"><img src="/UploadFiles/fck/images/tu(2).jpg" alt=""></figure><p>&nbsp;</p>', NULL, NULL, N'2019-03-12 10:32:51', N'1', N'2020-11-20', N'admin', N'185', NULL, NULL, N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'87F57705-2347-4ECB-9D1D-55977FE153A1', N'0', N'190', NULL, N'第五届全国高等学校化工类专业实验教学研讨会在我校顺利举行', N'<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2019年8月21日-8月24日，由教育部高等学校化工类专业教学指导委员会主办，我校承办的第五届全国高等学校化工类专业实验教学研讨会在朝晖校区顺利召开。海南大学张玉苍教授、天津大学夏淑倩教授、贵州大学林倩教授、广州大学梁红教授等教育部高等学校化工类专业教学指导委员会委员和来自全国26个省（直辖市、自治区）80所高校的250余位教师参加了会议。浙江工业大学校长李小年教授出席开幕式并致辞。</p>
<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 大会以&ldquo;建设实践教学金课，提升化工类专业人才培养质量&rdquo;为主题，邀请天津大学夏淑倩教授、香港科技大学Marshal YS LIU教授、清华大学卢滇楠教授、大连理工大学王维教授和浙江工业大学计伟荣教授作了5个精彩的大会报告。会议还设置了两个分会场，邀请了来自宁波诺丁汉大学、华东理工大学等12位教师分享了化工类实验教学改革、实验实践课程建设和自制实验仪器设备等内容。</p>
<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;会议期间，会议代表参观了我校化工原理实验室、化工专业实验室和计算机模拟实习基地，并就实验室安全、实验设施建设等话题展开了主题讨论与交流。</p>
<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 教指委委员张玉苍教授对会议进行了总结，从全国教育大会和本科教育工作会议出发，深入分析了实验教学对于培养具有国际竞争力的化工专业人才的重要意义。他指出，深化实验教学改革和提升实验教学质量是强化学生的创新精神、实践能力、社会责任感等核心素质的关键，广大实验教学一线教师责任重大。本次会议充分交流化工类专业实验教学体系、实验教学模式和内容、实验课程建设、实验室管理、实验室安全、实验教学资源整合等方面的经验和成果，探讨所面临的问题，进一步明确和规划了化工类专业实验教学发展方向。</p>
<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 本届全国高等学校化工类专业实验教学研讨会在我校顺利开展，对促进我校实验教学改革和提升实验教学水平具有积极意义。</p>
<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 据悉，第六届全国高等学校化工类专业实验教学研讨会将在西安交通大学举行。<img alt="" src="/UploadFiles/fck/images/EA4A2183_1(2).jpg" style="width: 780px; height: 378px;" /></p>', NULL, NULL, N'2019-08-27 09:51:21.000', N'1', N'2020-11-20', N'admin', N'186', NULL, NULL, N'0')
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'5107CCE7-4BC9-4ABE-85C7-2312447666DE', N'0', N'191', NULL, N'关于2019/2020（2）基础化学实验教学安排的通知', N'<p align="left">
	2018、2019级相关同学：</p>
<p align="left">
	&nbsp; &nbsp; &nbsp; &nbsp;首先问候大家，你在家乡安好！</p>
<p align="left">
	&nbsp; &nbsp; &nbsp; &nbsp;由于新冠肺炎疫情还没有完全结束，本学期开学推迟了，具体开学时间要等学校的通知。为开学以后，能尽快投入学习，同学们可通过网络预习本学期相关实验内容。</p>
<p align="left">
	&nbsp; &nbsp; &nbsp; &nbsp;1. 本学期开设实验内容为：</p>
<p align="left">
	&nbsp; &nbsp; &nbsp;（1）2019级基础化学实验（Ⅰ）-无机及分析化学实验：</p>
<p align="left">
	&nbsp; &nbsp; &nbsp; &nbsp;实验1&nbsp; 酸碱标准溶液的配置与比较</p>
<p align="left">
	&nbsp; &nbsp; &nbsp; &nbsp;实验2&nbsp; NaOH标准溶液的标定与有机酸相对分子质量的测定&nbsp;</p>
<p align="left">
	&nbsp; &nbsp; &nbsp; &nbsp;实验3&nbsp; HCl标准溶液的标定与工业烧碱含量的测定</p>
<p align="left">
	&nbsp; &nbsp; &nbsp; &nbsp;实验4 &nbsp;EDTA标准溶液的标定与水中钙镁总量的测定（配位滴定法）&nbsp;&nbsp;</p>
<p align="left">
	&nbsp; &nbsp; &nbsp; &nbsp;实验5&nbsp; KMnO4标准溶液的标定与过氧化氢含量的测定&nbsp;</p>
<p align="left">
	&nbsp; &nbsp; &nbsp; &nbsp;实验6&nbsp; Na<sub>2</sub>S<sub>2</sub>O<sub>3</sub>标准溶液的标定与CuSO<sub>4</sub>&middot;5H<sub>2</sub>O中Cu含量的测定</p>
<p align="left">
	&nbsp; &nbsp; &nbsp; &nbsp;实验7&nbsp; 邻二氮菲分光光度法测定铁&nbsp;</p>
<p align="left">
	&nbsp; &nbsp; &nbsp; &nbsp;实验8&nbsp; 醇同系物的气相色谱分离与含量测定&nbsp;</p>
<p align="left">
	&nbsp; &nbsp; &nbsp; &nbsp;实验9&nbsp; 考查实验（草酸样品含量的测定）</p>
<p align="left">
	&nbsp; &nbsp; &nbsp; （2）2018级基础化学实验（Ⅱ）-有机化学实验：</p>
<p align="left">
	&nbsp; &nbsp; &nbsp; &nbsp; 实验1&nbsp; 二苯基乙二酮的制备及薄层色谱</p>
<p align="left">
	&nbsp; &nbsp; &nbsp; &nbsp; 实验2&nbsp; 2-甲基-2丁醇的制备</p>
<p align="left">
	&nbsp; &nbsp; &nbsp; &nbsp; 实验3&nbsp; 乙酰二茂铁的制备及柱色谱分离</p>
<p align="left">
	&nbsp; &nbsp; &nbsp; &nbsp; 实验4&nbsp; 苯甲酸（微波）与苯甲酸乙酯的制备及减压蒸馏</p>
<p align="left">
	&nbsp; &nbsp; &nbsp; &nbsp; 实验5&nbsp; 苯甲醇与苯甲酸的制备</p>
<p align="left">
	&nbsp; &nbsp; &nbsp; （3）2018级基础化学实验（Ⅲ）-物理化学实验：</p>
<p align="left">
	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;实验1 乙酸乙酯皂化</p>
<p align="left">
	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;实验2 甲酸溴氧化</p>
<p align="left">
