INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'2D332132-FA4F-4249-AF22-799A2117869A', N'0', N'209', NULL, N'化学化工实验教学中心召开暑期工作会议', N'<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; 7月21日，化学化工实验教学示范中心（化学化工虚拟仿真实验教学中心）召开暑期教学会议，实验教学中心全体教师参加会议。</p>
<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 中心主任计伟荣教授总结了化学化工实验教学中心组建以来的工作进展。在中心全体教师的共同努力和学校、学院的大力支持下，化学化工实验教学中心入选了浙江省实验教学示范中心重点建设遴选项目，申报获批了国家级虚拟仿真实验教学中心和《基础化学实验》国家精品资源共享课，并为我校学生参加大学生基础化学实验竞邀请和化工设计竞赛等课外科技竞赛提供了有利支撑。计伟荣教授对推进中心的建设工作提出了几点要求：一是要重点做好中心顶层设计，深化教学模式改革，在兼顾中心规范性和开放性的基础上进一步整合资源、改进课堂教学模式；二是要重视实验教学改革的研究，中心建设已经从简单的规模扩张转变到内涵提升上来，其建设重点是质量提升，鼓励青年教师勇于创新，积极将个人发展融入到中心建设和学科发展中来；三是强调继续以开放的心态做好中心建设工作，支持广大教师走出校门积极参加国家级省级化学化工实验教学示范中心建设的交流研讨会，并鼓励教师个人联系其他高校开展交流与合作。</p>
<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 中心副主任艾宁、强根荣、杨阿三和刘华彦分别发言。与会教师对中心建设过程中所遇到诸多问题进行了探讨，并拟定了初步解决方案。</p>', NULL, NULL, N'2014-07-24 11:17:34.000', N'1', N'2020-11-20', N'admin', N'205', NULL, NULL, NULL)
