INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'429EF4E0-B159-43A9-BBA6-94090B700288', N'0', N'214', NULL, N'浙江省高校虚拟仿真实验教学中心建设专题研讨会', N'<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2014年6月6日，由浙江省高校实验室工作研究会举办的浙江省高校虚拟仿真实验教学中心建设专题研讨会在浙江理工大学举行，由教育厅高教处领导解读了国家级虚拟仿真实验中心建设文件，2013年浙江省入选国家级虚拟仿真实验教学中心的单位负责人做了专题报告，并实地参观了浙江理工大学服装虚拟仿真实验教学中心和国家级实验教学示范中心、杭州电子科技大学电子信息技术虚拟仿真实验中心和国家级实验教学示范中心。本中心教师一行四人参加了本次专题研讨会，中心常务副主任艾宁教授在会上做了化学化工虚拟仿真实验教学中心建设进展报告，介绍了中心的发展历史、现状和后续的建设思路。</p>', NULL, NULL, N'2014-06-09 09:23:08.000', N'1', N'2020-11-20', N'admin', N'210', NULL, NULL, NULL)
