INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'06D9DCA0-2558-4D80-9B7B-2FA1174E2D9A', N'0', N'215', NULL, N'我校教师参加高等学校国家级实验教学示范中心十周年建设成果展示交流会', N'<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 9月22~25日，高等学校国家级实验教学示范中心十周年建设成果展示交流会在福州海峡国际会展中心举行。本次展会在教育部高教司领导下，由高等学校国家级实验教学示范中心联席会负责组织。我校计伟荣教授代表化学化工国家级实验教学示范中心、国家级虚拟仿真实验教学中心在&ldquo;化学、生命、材料&rdquo; 组作交流报告，向与会代表报告了我校在&ldquo;强化实践育人效果，提高工科专业人才培养质量&rdquo;方面的实践情况。</p>
<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 实验教学示范中心建设于2004年底~2005年初启动。在&ldquo;十一五&rdquo;和&ldquo;十二五&rdquo;期间，化学化工学科共完成58个国家级实验教学示范中心、19个国家级虚拟仿真实验教学中心的建设。通过项目建设，极大地推动了我国高等学校实验教学改革和实验条件改善，进一步完善了高校实验教学管理体系，促进了高校优质教学资源的整合、共享，提升了学校办学水平和教育质量，有力地推动了学生动手能力、实践能力和创新能力培养，对大学生创业、国家科技创新、社会经济可持续发展等起到了重要的促进作用。</p>', NULL, NULL, N'2015-09-25 10:17:38.000', N'1', N'2020-11-20', N'admin', N'211', NULL, NULL, NULL)
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'CA90364C-29CB-4461-8226-443BD57F8071', N'0', N'216', NULL, N'化工原理实验室开展新学期教学研讨活动', N'<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 9月16日，为进一步提高青年教师的教学能力，化工原理实验室在西配楼105实验室举行了教学研讨活动，共有8名担任化工原理实验教学的老师参加。</p>
<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 本次研讨活动以化工传热综合实验为主题实验，分新教师试讲、老教师点评指导和新老教师共同讨论教学过程中的问题三个部分。首先新教师苏利伟老师通过教学PPT讲解了实验目的、实验原理、实验流程和实验操作注意事项等内容，还增加了工业上传热设备的拓展介绍，内容详实。大家就苏利伟老师的讲课情况，充分肯定了他精心编排的教学PPT和细致耐心的讲解，同时也指出其在实验中的教学重点与难点安排、语音语调和与学生交流互动等方面的不足，并提出了一些提高实验教学质量的具体意见和建议。接着，应惠娟老师和俞云良老师介绍了实验装置上的一些结构原理并再次强调了实验教学过程中的注意事项。最后新老教师就实验准备、实验讲解、实验装置、实验操作和数据处理过程中的具体问题，进行了深入的交流与探讨。</p>
<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 化工原理实验是化工及其相关工科专业的重要专业基础实践课，理论性高，实用性强，对培养学生的工程概念、定量计算、实验技能、设计创新能力以及分析和解决实际问题的能力具有十分重要的作用。通过本次教学研讨活动，新老教师均受益匪浅。大家一致认为：这种教学研讨和互动交流，十分有利于提升教师的业务水平和教学技能，进而提高实验教学质量。</p>
<p>
	&nbsp;</p>
<p>
	<img src="http://www.ce.zjut.edu.cn/ueditor/jsp/upload/image/20190917/1568688861812024843.png" title="1568688861812024843.png" /></p>
<p>
	&nbsp; &nbsp; &nbsp; &nbsp; 姬登祥老师主持本次教学研讨活动&nbsp;<img src="http://www.ce.zjut.edu.cn/ueditor/jsp/upload/image/20190917/1568688861828038086.png" title="1568688861828038086.png" /></p>
<p>
	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; 苏利伟老师进行《化工传热综合实验》试讲&nbsp;&nbsp;<img src="http://www.ce.zjut.edu.cn/ueditor/jsp/upload/image/20190917/1568688861859003787.png" title="1568688861859003787.png" /></p>
<p>
	&nbsp; &nbsp; &nbsp; &nbsp;&nbsp; 卢美贞老师就听课情况提问指导</p>', NULL, NULL, N'2019-12-30 14:28:20.000', N'1', N'2020-11-20', N'admin', N'212', NULL, NULL, NULL)
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'C8220D5E-1CD5-4554-BC48-B0A4B8D5E8B3', N'0', N'217', NULL, N'我校学生在第八届大学生化工设计竞赛全国总决赛中获奖', N'<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2014年8月17-20日，由中国化工学会、中国化工教育协会、教育部高等学校化学工程与工艺专业教学指导分委员会主办的第八届大学生化工设计竞赛全国总决赛在常州大学落幕。由刘树、江泉达、陶志伟、张博和张源岑五位同学组成的&ldquo;Flying team&rdquo;团队代表我校参加全国总决赛并获得全国一等奖，同时以华东赛区总决赛第四名的成绩获得华东赛区特等奖。</p>
<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 此外，经过前期省赛的网评、会评和答辩，我校由单国明、杜立辉、周超俊、金杞糠和陈雷组成的&ldquo;NOVA&rdquo;团队获得省一等奖，章林炯、陆可丹、徐玲倩、周炳和孙伟晔组成的&ldquo;Super wingteam&rdquo;团队获得省三等奖。</p>
<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 中心多方面积极参与，赛前为学生的培训提供场地，竞赛过程中，中心机房为竞赛提供软件支持，中心教师参与竞赛指导，有力配合、支持了校级、省级、华东区和全国化工设计竞赛工作。</p>', NULL, NULL, N'2014-08-28 14:25:19.000', N'1', N'2020-11-20', N'admin', N'213', NULL, NULL, NULL)
GO

