INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'7E0CFF7A-639D-4168-BB67-00EA646CBF73', N'0', N'219', NULL, N'化学化工虚拟仿真实验教学中心教师参加化工、制药与生物工程类专业工程教育国际研讨会', N'<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2014年10月11~12日，由中国化工教育协会和北京化工大学主办的化工、制药与生物工程类专业工程教育国际研讨会在北京化工大学举行。本次会议主要围绕卓越工程人才培养（特别是化工、制药与生物工程类专业）、基于企业全球化的国际联合培养以及基于工程和创新的产学研合作教育、&ldquo;卓越工程师教育培养计划&rdquo;与《华盛顿协议》等国际工程专业认证体系的衔接等主题展开研讨，来自美国工程技术认证委员会（ABET）、伍斯特理工学院、法国巴黎国立高等化工大学、美国石溪大学、法国国立高等化学技术工艺工程师学院、德国汉堡工业大学，以及国内天津大学、华南理工大学、华东理工大学、北京化工大学、浙江工业大学、南京工业大学等高校做了特邀报告。中心一行四人参加了本次国际研讨会，我校教务处处长、中心主任计伟荣教授做了&ldquo;基于卓越化学工程师培养的实践教学体系改革&rdquo;特邀报告。</p>
<p>
	<img alt="" src="/UploadFiles/fck/images/未标题-1(1).jpg" style="width: 546px; height: 317px" /></p>', NULL, NULL, N'2014-10-15 12:28:54.000', N'1', N'2020-11-20', N'admin', N'215', NULL, NULL, NULL)
