INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'56D63965-464C-4E9B-A3D9-3339AE4D3CC8', N'0', N'220', NULL, N'化学化工实验教学战疫情  停课不停学', N'<p>
	&nbsp; &nbsp; &nbsp; &nbsp;实验教学课程情况比较特殊，既要有扎实的实验理论知识，更要有解决实际问题的动手能力。为抗战疫情，化学化工实验教学根据课程的特点，充分利用化学化工国家级虚拟仿真实验教学中心、基础化学实验国家级精品资源共享课程的网上资源，并拍摄实验绪论与理论视频，利用超星学习网络平台等，积极开展线上教学，停课不停学。</p>
<p>
	&nbsp; &nbsp; &nbsp; &nbsp;近年来，化学化工实验教学中心坚持&ldquo;以实为本、以虚补实、虚实结合、互补提高&rdquo;的基本原则，结合学科专业特点和人才培养目标，不断充实和精心组织虚拟仿真实验教学资源，特别贯彻了虚实结合、软硬一体、教学与科研有机结合、学校与社会协同建设、实验教学与社会应用相互促进和第一课堂与第二课堂相互支撑等虚拟仿真实验教学资源建设思路。中心拥有课程试题库、多媒体课件素材库、仿真实验软件、仿真实训软件和课程设计软件等化学化工类虚拟仿真教学资源共52套。利用这些虚拟仿真实验教学资源，共开设近百个虚拟仿真实验项目，支撑19门课程教学和化学实验竞赛、化工设计竞赛、节能减排竞赛、&ldquo;挑战杯&rdquo;大学生课外科技学术作品竞赛、大学生创业计划竞赛等10类学生课外科技活动。同时，实验中心针对虚拟仿真项目还自主录制了12个微课，校园网络平台全年24小时开放，学生可以自由安排时间利用仿真软件自主学习。在实验教学中部分实验项目引入二维码，为学生创造良好的泛在化学习环境。</p>
<p>
	&nbsp; &nbsp; &nbsp; &nbsp;针对当前的疫情，为了能在开学以后尽快投入学习，实验中心在2月14日即在网站上发出通知，公布本学期的基础化学实验教学内容，实验指导教师通过各自任教班级的微信群、QQ群等途径通知到每位学生，要求学生充分利用中心网站（<a href="http://hxsyzx.dodokon.com/">http://hxsyzx.dodokon.com</a>）和基础化学实验国家精品资源共享课程&ldquo;爱课程&rdquo;网（<a href="http://www.icourses.cn/sCourse/course_2619.html">http://www.icourses.cn/sCourse/course_2619.html</a>）的资源，预习相关内容，学习相关实验。</p>
<p>
	&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;在国家级虚拟仿真实验教学示范项目《合成氨生产综合仿真实习》的建设中，已将项目网站升级并托管于高校教育出版社的&ldquo;实验云&rdquo;，进一步提高了网络课程的稳定性，同时，实验中心教师年定制开发的《合成氨3D认知实习仿真软件》，可配套应用于《合成氨生产综合仿真实习》教学项目，进一步拓展虚拟仿真教学资源。此外，中心《二甲醚生产半实物化工仿真工厂》实验教学项目本年度首次开课，引入半实物仿真技术，采用虚实结合的教学手段，计16学时/人。</p>
<p>
	&nbsp;</p>
<p>
	&nbsp;</p>
<p>
	&nbsp;</p>', NULL, NULL, N'2020-02-21 17:24:29.000', N'1', N'2020-11-20', N'admin', N'216', NULL, NULL, NULL)
GO

