INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'89CBBEAB-90ED-45B1-8AD1-CE03770897CC', N'0', N'222', NULL, N'化学实验教学中心被评为校级优秀基层教学组织', N'<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 为深化学校教育教学改革，推进本科教育&ldquo;品质教学&rdquo;工作，不断提高教学质量，学校组织评选了2017年度优秀基层教学组织。经基层教学组织负责人申报，学院推荐，校内外专家评审，确定全校15个基层教学组织为2017年度校级优秀基层教学组织，化学实验教学中心名列其中。</p>
<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 化学实验教学中心2017年承担了近20个专业、2038人的基础化学实验教学任务，实验人时数183368。支撑了化学工程与工艺、食品科学与工程、安全工程等专业的专业认证与复评等工作。</p>
<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 作为浙江省大学生化学竞赛秘书处，实验中心协调、指导第九届浙江省大学生化学竞赛的具体事宜及命题审查等，中心教师还指导了本校学生参赛。</p>
<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 中心承担包括省教改、省课改在内的教学改革项目8项，教学改革的成果在《浙江教育报》（2017年3月27日）、《中国教育报》（2017年7月24日）、中国教育新闻网（<a href="http://www.jyb.cn/zgjyb/201707/t20170724_716583.html">http://www.jyb.cn/zgjyb/201707/t20170724_716583.html</a>）、中国教育和科研计算机网（<a href="http://www.edu.cn/ke_yan_yu_fa_zhan/gao_xiao_cheng_guo/gao_xiao_zi_xun/201707/t20170724_1542771.shtml">http://www.edu.cn/ke_yan_yu_fa_zhan/gao_xiao_cheng_guo/gao_xiao_zi_xun/201707/t20170724_1542771.shtml</a>）等媒体得到宣传、报道。积极开展教研活动，参加由中国高教学会、国家级实验教学示范中心联席会组织的国内教学会议8人次。</p>
<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 中心对新引进的教师和新聘任的实验指导教师通过集体备课、讲课示范等形式进行培训，促进教学质量的提高。同时，采用&ldquo;走出去、请进来&rdquo;等多种方式，积极吸取国内院校的先进教学理念、教学方法，改进实验教学手段。</p>
<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 中心在做好日常教学工作的同时，积极开展对外交流。协助省总工会做好全省职工技能大赛化学检验工比赛，以及衢州市化学检验工比赛的命题、裁判等工作，拓展示范中心的功能，为地方经济和社会发展服务。协助做好中学生化学奥林匹克竞赛工作，为浙江省杭州第二中学、杭州学军中学参加省级选拔赛培训实验。杭二中学生21人、学军中学9人获第31届中国化学会奥林匹克（初赛）浙江赛区一等奖。杭二中学生在第31届中国化学奥林匹克决赛中取得4块金牌、1块银牌的优异成绩。</p>
<p>
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2017年实验中心任课教师承担的教学班级均获优课优酬奖励。</p>', NULL, NULL, N'2018-03-05 09:27:39.000', N'1', N'2020-11-20', N'admin', N'218', NULL, NULL, NULL)
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'26CADEFB-F406-4143-A5CC-11A428B37863', N'0', N'223', NULL, N'化工实验教学中心被评为校级优秀基层教学组织', N'<p>
	&nbsp; &nbsp; &nbsp; 为深化学校教育教学改革，推进本科教育&ldquo;品质教学&rdquo;工作，不断提高教学质量，学校组织评选了2018年度优秀基层教学组织。经基层教学组织负责人申报，学院推荐，校内外专家评审，确定全校16个基层教学组织为2018年度校级优秀基层教学组织，化工实验教学中心名列其中。</p>
<p>
	&ensp;&nbsp; &nbsp; 化工实验教学中心2018年完成了近20个专业、2000余人的化工基础实验、化工专业实验和化工原理实验的教学任务，实验人时数44368，计算机模拟基地约4万机时，还支撑了制药工程和材料科学与工程等专业的专业认证与复评等工作，同时实验中心任课教师获优课优酬比例为81.8%。</p>
<p>
	&nbsp; &nbsp; &nbsp; 中心艾宁教授负责的《合成氨生产综合仿真实习》虚拟仿真实验教学项目获教育部高等教育司的2017-2020年国家级示范性虚拟仿真实验教学项目建设的首批项目认定，为学校取得首个项目突破。此外，中心教师积极承担包括省教改、省课改在内的教学改革项目5项，校级教改14项，发表教学论文3篇，申请专利13项，自制或改装仪器设备5项。</p>
<p>
	&nbsp; &nbsp; &nbsp; 中心积极开展教研活动，参加由中国高教学会、国家级实验教学示范中心联席会组织的国内教学会议21人次，在大会上做报告近10次。中心还努力塔建与校外企业的交流平台，全年总计32人次，同时2018年还接待了西北大学、复旦大学等20余所高校来访交流。</p>
<p>
	&nbsp; &nbsp; &nbsp;中心安全工作突出，开放共享成效明显。中心教师指导学生创新训练项目国家级1项，校级2项，参加竞赛获全国特等奖1项，全省二等奖2项，三等奖1项，此外，张建庭老师还指导学生徐林获得2018届校级优秀本科学位论文。</p>', NULL, NULL, N'2019-01-24 11:00:32.000', N'1', N'2020-11-20', N'admin', N'219', NULL, NULL, NULL)
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'EB9EE693-618B-4915-88B4-16F95DA0E4F0', N'0', N'224', NULL, N'化学化工实验教学中心在学院分项评优工作中获得佳绩', N'<p>
	&nbsp; &nbsp; &nbsp; &nbsp;为推动学院任期目标的实现和研究型学院的建设，通过树立典型、弘扬先进来调动和激发全院教职工的积极性和创造力，化学工程学院进行了2018年度学科分项工作评优的评选活动，化学化工实验教学中心在此次分项评优工作中获评人才培养优秀奖和&ldquo;平安化工&rdquo;建设工作鼓励奖。</p>
<p>
	&nbsp; &nbsp; &nbsp; &nbsp; 此次评选结果是经各学科、系（实验中心）自由申报，PPT汇报，学院党政联席会议成员、学科负责人、党总支（直属支部）书记和系（实验中心）主任进行投票表决，最后经学院党政联席会议审议产生的。</p>
<p>
	&nbsp; &nbsp; &nbsp; &nbsp; 学院评优结果链接：</p>
<p>
	http://www.ce.zjut.edu.cn/jsp/news.jsp?neId=1592</p>', NULL, NULL, N'2019-01-24 10:59:13.000', N'1', N'2020-11-20', N'admin', N'220', NULL, NULL, NULL)
GO

INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'2403DA59-DF6C-41E7-A344-C2BF2811BF59', N'0', N'225', NULL, N'关于疫情期间生产实习仿真开课的通知', N'<p>
