INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'27DC20B0-4055-484D-88E0-F087BC502D4B', N'0', N'230', NULL, N'中心教师参加第四届化学工程与工艺专业实验教学研讨会', N'<p align="left" style="margin-left: 5.25pt;">
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 2018年8月15日-17日，第四届化学工程与工艺专业实验教学研讨会在银川举行。中心艾宁、杨阿三、姬登祥、许轶、应惠娟、李琰君、王祁宁和蔡金法等8位老师参加了本次会议。</p>
<p align="left" style="margin-left: 5.25pt;">
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 中心艾宁教授在大会上作了《化学化工实验室安全管理的思考》的报告，就高校实验室安全事故现状、安全事故原因分析和高校实验室安全管理举措等内容进行了详细介绍，受到了与会代表的广泛关注与认可。同时，中心与会教师并就我校化工类基础实验和专业实验教学体系构建、虚拟仿真实验应用、实验室安全管理和实验室教师队伍建设等主题与其他院校作了经验交流。</p>
<p align="left" style="margin-left: 5.25pt;">
	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 此次研讨会由教育部高等学校化工类专业教学指导委员会主办、北方民族大学化学化学工程学院承办，来自天津大学、清华大学、浙江大学、大连理工大学等全国近60所高校在内的207位教师代表和企业代表参加了会议。大会共作了9个专题报告，并就实验教学、实验室建设等主题进行了分组讨论与交流，现场气氛十分热烈。期间，与会代表还参观了北方民族大学化学与化学工程学院工程训练中心。</p>
<p align="left" style="margin-left: 5.25pt;">
	&nbsp;<img alt="" src="http://192.168.0.226:4062/UploadFiles/fck/images/20180824-1.jpg" style="width: 700px; height: 442px;" /></p>
<p align="left" style="margin-left: 5.25pt;">
