INSERT INTO [dbo].[News] ([NewsUuid], [IsDeleted], [ID], [NewSubhead], [NewsTitle], [NewsContent], [NewsTypeUuid], [NewsPic], [NewsUrl], [Staue], [AddTime], [AddPeople], [SortID], [video], [File], [IsStick]) VALUES (N'B16E6144-2C24-409F-B0B2-4A2D86A04B7F', N'0', N'231', NULL, N'莫干山，我们来了｜化学化工实验中心启动实验室搬迁工作', N'<p style="text-align:center;">齐齐心协力，搬迁启动</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; 新学期伊始，化学化工实验教学中心即投入到紧张的搬迁工作中。近日，实验室与资产管理处、学院负责人陪同副校长虞晓芬一行来实验中心慰问忙碌中的中心老师。虞晓芬认真察看了搬迁中的实验室，听取了实验中心搬迁整体情况，详细了解了搬迁过程中存在的困难，对中心老师辛劳的付出给予肯定。目前，化工原理实验室已于9月7日完成实验设备拆装、打包环节，并于9月11日完成部分搬迁。9月18日将进行学院机房搬迁，9月24日至26日将进行后批次实验设备搬迁。</p><p style="text-align:center;">凝聚共识，办法总比困难多</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 化学化工实验中心负责人强根荣老师谈到，莫干山校区实验室建设时间紧、困难多，各个工序的施工队交叉作业，实验室内部的水电、弱电、通风、消防设施、安全设施等管网交叉复杂，必须要通盘考虑设计。为此，中心教师在2018年暑期就前往多地了解考察相关的学校和实验室建设装备公司，学习建设经验，厘清注意事项。在今年暑期实验室安装建设过程中，实验室与资产处及学院相关领导给予了充分协调和极大支持。中心安排值班表，每隔一天就有一位老师前往工地察看进度，及时发现、纠正建设中的问题。中心承担着全校化学化工的实验教学任务，涉及面广、仪器设备多，低值易碎的玻璃仪器必须逐一包扎，而一些大型设备无法整体搬迁，必须分拆后到新校区重新安装，投入的财力、人力都是巨大的。因此，中心举全员之力统一协调，群策群力，努力寻找可解决的方案.</p><p style="text-align:center;">未来可期，搬好更要用好</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp; &nbsp;“尽快搬、尽快用”，当然从“搬过来”到“用起来”，还有许多工作要完成。聚焦本次实验室搬迁工作，化工人始终做“艰苦创业、开拓创新、争创一流”的“三创精神”守护者、践行者，必将努力完成从杭州校区到莫干山校区近40公里的接力跑活动。莫干山校区，我们来了！</p><p>&nbsp;</p>', NULL, N'20201207_162000960_c26189b7-7615-45f0-a12a-876545b36e70.jpg', N'2020-09-23 14:04:33', N'1', N'2020-11-20', N'admin', N'227', NULL, NULL, NULL)
GO

