INSERT INTO [dbo].[Proclamation] ([ProclamationUuid], [IsDeleted], [ID], [Staue], [AddTime], [AddPeople], [ProclamationTitle], [SortID], [Remark], [ProclamationContent], [IsStick]) VALUES (N'6E92CC6E-D6FB-48E8-9A25-41BD1EB1371E', N'0', N'10', NULL, N'2011-11-07', N'admin', N'徐铜文教授讲座预告', N'12', N'', N'<h2 style="margin-left:0px;text-align:center;"><strong>徐铜文教授讲座预告</strong></h2><p>发表于: 2011/11/7 9:52:18</p><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp; 题目：创新实现理想&nbsp;&nbsp;<br>　　讲座人：徐铜文教授<br>　　时间：11月7日（周一）上午10:00-11:30&nbsp;&nbsp;<br>　　地点：邵科馆国际会议厅&nbsp;&nbsp;<br>　　讲座人简介：<br>　　徐铜文，博士，中国科学技术大学教授、博士生导师，国家杰出青年基金获得者。中国电驱动膜联谊会发起人，中国膜工业协会专家委员会成员，中国膜技术标准化委员会委员、国家自然基金委化学部第13届专家评审组成员，J. Membr. Sci.等9种国际期刊编委、执行（客座）编辑。目前研究方向为离子交换膜制备及其应用，多项成果获得中试或实施产业化,现正在承担国家自然科学基金杰出和面上基金各一项，国家重大基础研究计划课题一项等。作为通讯作者在Progress in Polymer Sci.等国内外高水平杂志发表论文160余篇，SCI他引1400余次，H因子为27；以第一贡献人身份获省部级（含行业协会）一等奖4次、二等奖2次；申请发明专利28项（18项授权）。</p><p>&nbsp;</p>', N'0')
GO

INSERT INTO [dbo].[Proclamation] ([ProclamationUuid], [IsDeleted], [ID], [Staue], [AddTime], [AddPeople], [ProclamationTitle], [SortID], [Remark], [ProclamationContent], [IsStick]) VALUES (N'EBA155CB-52DA-49FA-8D9A-66B2AE760BAD', N'0', N'11', NULL, N'2011-11-02', N'admin', N'I-Ming Chou教授学术报告预告', N'13', N'', N'<h2 style="margin-left:0px;text-align:center;"><strong>I-Ming Chou教授学术报告预告</strong></h2><p>发表于: 2011/11/2 20:52:32</p><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 主题：In situ spectroscopic analyses of solids and fluids in optical cells at various pressure-temperature conditions<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 时间：11月4日(星期五)&nbsp; 上午10：00<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 地点：邵科馆国际会议厅<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 主讲人简介：<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; I-Ming Chou,美国地质调查局资深科学家，主要从事地质流体性质、二氧化碳捕集储存技术、天然气水合物、海底沉积物、海底热液等流域研究。已在包括Science在内的国际学术刊物上发表学术论文180余篇，发表各类摘要文章 130余篇。他曾任North America Chinese Earth Scientists Association主席(1998-1999)， Overseas Chinese Environmental Engineers and Scientists Association主席(2000－2001)，现任Overseas Chinese Earth Science and Technology Association主席(1998至今)。</p><p>&nbsp;</p>', N'0')
