INSERT INTO [dbo].[Proclamation] ([ProclamationUuid], [IsDeleted], [ID], [Staue], [AddTime], [AddPeople], [ProclamationTitle], [SortID], [Remark], [ProclamationContent], [IsStick]) VALUES (N'19787D82-9F9D-481E-B91A-C8DFB2C58314', N'0', N'12', NULL, N'2011-06-29', N'admin', N'关于《综合化学实验》选修课的通知', N'14', N'', N'<h2 style="margin-left:0px;text-align:center;"><strong>关于《综合化学实验》选修课的通知</strong></h2><p>发表于: 2011/11/2 9:37:37</p><p style="margin-left:0px;">各位同学：<br>&nbsp;&nbsp;&nbsp; 下学期选修课报名已开始，化学实验教学中心将在下学期继续开设《综合化学实验》选修课，总学时60学时，学分2分。综合化学实验室地址在存中楼308。欢迎全校大二、大三的相关同学选修。<br>&nbsp;&nbsp;&nbsp; 本课程的基本情况如下：<br>&nbsp;&nbsp;&nbsp; 一、基本指导思想<br>&nbsp;&nbsp;&nbsp; 综合化学实验（Comprehensive Chemical Experiments）是在学生掌握实验基本原理、基本操作的基础上，在化学一级学科层面上安排的，与科学前沿紧密结合的，旨在提高学生综合运用基础知识和基本技能，培养学生科研素质和创新能力的实验课。<br>综合实验的基本要求，就是将比较多的基本理论、基本实验技能融合贯通在一个实验中，使实验内容成为综合联系化学、材料、生命、环境、能源、药学等学科，反映化学各二级学科重大进展、前沿和交叉领域，集合成、分离与提纯、物理性质测定、化学性质研究、结构表征、性质解释等为一体，具有综合性、系统性、创新性。<br>基于综合实验的综合性与创新性，其内容可分为综合性实验和创新性实验两个层次，前者为跨学科、多技能综合训练，后者则与科研课题或生产实际密切结合。学生在教师指导下独立进行综合和创新性研究，真正感受从事化学研究的真谛，培养其爱科学、学科学、用科学的积极性和主动性，培养其科学素养，提高其进行科学研究的能力。<br>&nbsp;&nbsp;&nbsp; 二、目的与要求：<br>&nbsp;&nbsp;&nbsp; 综合化学实验是按照实验教学“一体化、多层次”及由“专业教学”改为“通才教学”和适度的专业教学相结合的指导思想，面向高年级本科生开设的一门独立的实验课。<br>&nbsp;&nbsp;&nbsp; 基于培养学生综合化学实验技能的重要性，本课程着重于促进本科生在化学学习中手脑并重、手脑并用、手脑协调，强调培养创新精神、创新意识、创新能力，综合运用各方面的知识、各种实验手段以完整地解决实际问题，结合学生情况因材施教，培养科学思维方法和训练科研能力，作为基础实验课（着重知识学习和能力训练）与科学研究（以已知解决未知）间的衔接、过渡。<br>&nbsp;&nbsp;&nbsp; 教学目的：1. 全面复习基础化学实验基本技术、巩固提高实验基本技能。<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;2. 学习从事科学研究的基本过程，提高综合分析问题、解决问题的能力。<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;3. 综合运用各种化学实验技术和基础知识，培养创新意识、科学方法、科学精神等科学工作者的基本素质。<br>&nbsp;&nbsp;&nbsp; 基本要求：要求学生经历查阅文献、设计实验方案、讨论交流、独立或协作完成实验、撰写学术论文等教学环节，并初步掌握各种现代分析仪器的使用。</p><p style="margin-left:0px;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 化学实验教学中心<br>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;2011年11月2日</p><p>&nbsp;</p>', N'0')
